internal edit NoYes editCollectionsWriteOffBaseAmountSalesTax(
    boolean _set,
    CustParameters _custParameters,
    NoYes _value)
{
    NoYes ret;
    if (!_set)
    {
        ret = (CustParameters::isWriteOffBaseAmountSalesTaxEnabled(_custParameters)) ? NoYes::Yes : NoYes::No;
    }
    else
    {
        switch(_value)
        {
            case NoYes::Yes:
                _custParameters.CollectionsWriteOffBaseAmountSalesTax = DefaultNoYes::Yes;
                break;

            case NoYes::No:
                _custParameters.CollectionsWriteOffBaseAmountSalesTax = DefaultNoYes::No;
                break;
        }
        ret = _value;
    }
    return ret;
}

