public void modified()
{
    CustSettleChangePolicy_RU custSettleChangePolicy = new CustSettleChangePolicy_RU();

    if (custParameters.CustTaxation_RU != custParameters.orig().CustTaxation_RU &&
        custParameters.RecId)
    {
        try
        {
            switch (custParameters.CustTaxation_RU)
            {
                case CustTaxation_RU::OnPayment :
                    custSettleChangePolicy.changePolicy(NoYes::No); // to onPayment
                    break;

                case CustTaxation_RU::OnDelivery :
                    custSettleChangePolicy.changePolicy(NoYes::Yes); // to onShipment
                    break;
            }
            custParameters.update();
        }
        catch(Exception::Error)
        {
            custParameters.CustTaxation_RU = custParameters.orig().CustTaxation_RU;
            custParameters.doUpdate();
        }
    }
}

public boolean validate()
{
    boolean ret = super();

    if (SalesBookTable_RU::existActiveBook())
    {
        return checkFailed("@GLS103061");
    }
    if (! CustParameters::taxationAllowEdit_RU())
    {
        return checkFailed("@GLS110967");
    }

    if (custParameters.CustTaxation_RU != custParameters.orig().CustTaxation_RU &&
        Box::okCancel("@GLS103084", DialogButton::Cancel) != DialogButton::Ok)
        return false;

    return ret;
}

