/// <summary>
/// Decreases the priority of the selected transaction type.
/// </summary>
/// <remarks>
/// It updates record in <c>CustSettlementPriority</c> table and then refreshes the listbox control holding transaction types.
/// </remarks>
void clicked()
{
    str menuItemStr;
    MenuFunction menuFunction;
    Args args = new Args();

    FormRun formRun;
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        //Configure the form name pulled from the database.
        args.name(formStr(CustSettlementPrioritySetup));
        //Set the form mode
        args.parmEnumType(enumNum(FormOpenMode));
        args.parmEnum(FormOpenMode::ForNew);

        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait(true);

        custParameters_ds.reread();
        custParameters_ds.refresh();
        custParameters_ds.executeQuery();
    }
    else
    {
        menuItemStr = menuitemDisplayStr(CustSettlementPrioritySetup);
        menuFunction = new MenuFunction(menuItemStr, MenuItemType::Display);
        menuFunction.run(args);
    }
}

