public Common lookupReference()
{
    Query query = new Query();
    QueryBuildDataSource teamDs;
    SysReferenceTableLookup lookup = SysReferenceTableLookup::newParameters(tableNum(OMTeam), this, true);

    lookup.addLookupfield(fieldNum(OMTeam, Name));
    lookup.addLookupfield(fieldNum(OMTeam, Description));

    teamDs = query.addDataSource(tableNum(OMTeam));
    teamDs.fields().addField(fieldNum(OMTeam, Name));
    teamDs.fields().addField(fieldNum(OMTeam, Description));
    teamDs.addSortField(fieldNum(DirPartyTable, Name), SortOrder::Ascending);
    teamDs.addRange(fieldNum(OMTeam, IsActive)).value(int2str(true));

    lookup.parmQuery(query);

    return lookup.performFormLookup();
}

public Common resolveReference()
{
    DirPartyName name = this.filterValue(AbsoluteFieldBinding::construct(fieldStr(OMTeam, Name), tableStr(OMTeam))).value();
    OMTeam omTeam;

    select firstonly omTeam
        where omTeam.Name == name && omTeam.IsActive == true;

    return omTeam;
}

