public void clicked()
{
    super();

    custParameters.SalesCycle = salesCycle_salesCycle.value();
    custParameters.write();

    if (salesCycle_salesCycle.value())
    {
        element.updateSalesCycle(true);
        salesPurchCycle.createRecords(SalesPurchCycleModuleParameter::CustParameter);
    }
    else
    {
        element.updateSalesCycle(false);
        SalesPurchCycle::deleteSalesPurchCycle(SalesPurchCycleModuleParameter::CustParameter, '');
    }
    salesPurchCycle_ds.executeQuery();
}

public void clicked()
{
    SalesPurchCycle salesCycle;
    salesCycle = element.args().record(salesPurchCycle);

    super();

    switch (salesCycle.Action)
    {
        case SalesPurchCycleAction::PickingList:
            if (this.value())
            {
                SalesPurchCycle::salesPurchCycleUpdateActive(true,
                                                            SalesPurchCycleModuleParameter::CustParameter,
                                                            SalesPurchCycleAction::PickingListRegistrate,
                                                            salesCycle);
            }
            else
            {
                SalesPurchCycle::salesPurchCycleUpdateActive(false,
                                                            SalesPurchCycleModuleParameter::CustParameter,
                                                            SalesPurchCycleAction::PickingListRegistrate,
                                                            salesCycle);
            }
            salesPurchCycle_ds.research();
            break;
        case SalesPurchCycleAction::PickingListRegistrate:
            if (this.value())
            {
                SalesPurchCycle::salesPurchCycleUpdateActive(true,
                                                            SalesPurchCycleModuleParameter::CustParameter,
                                                            SalesPurchCycleAction::PickingList,
                                                            salesCycle);
            }
            else
            {
                SalesPurchCycle::salesPurchCycleUpdateActive(false,
                                                            SalesPurchCycleModuleParameter::CustParameter,
                                                            SalesPurchCycleAction::PickingList,
                                                            salesCycle);
            }
            salesPurchCycle_ds.research();
            break;
        default:
            salesPurchCycle_ds.refresh();
            break;
    }
}

