public boolean validate()
{
    boolean ret = super();

    // if the customer account number sequence been modified to a number sequence with different scope, display warning message.
    if (ret && ExtendedTypeNum(CustAccount) == NumberSequenceDatatype::find(NumberSequenceReference.orig().NumberSequenceDatatype).DatatypeId)
    {
        RefRecId oldNumSeq = NumberSequenceReference.orig().NumberSequenceId;
        RefRecId newNumSeq = NumberSequenceReference_NumberSequenceId.value();

        if (NumberSequenceTable::find(oldNumSeq).NumberSequenceScope != NumberSequenceTable::find(newNumSeq).NumberSequenceScope)
        {
            info("@AccountsReceivable:MessageUpdateNumSeqForCustAccount");
        }
    }

    return ret;
}
