void enableFields()
{
    custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, IsSEPA)).allowEdit(this.editIsSEPA());
    custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, DirectDebit)).allowEdit(this.editDirectDebit());

    element.enableFileFormatFields(!custPaymModeTable.UseGERConfiguration, !custPaymModeTable.UseGERImport);
}

/// <summary>
///     Checks for the payment type on <c>CustPaymModetable</c>.
/// </summary>
/// <returns>
///     true if payment type is electronic payment; otherwise, false.
/// </returns>
protected boolean editDirectDebit()
{
    return CustPaymModeTable.PaymentType == CustPaymentType::ElectronicPayment;
}

/// <summary>
///     Checks for the payment type on <c>CustPaymModetable</c>.
/// </summary>
/// <returns>
///     true if payment type is bill of exchange or electronic payment; otherwise, false.
/// </returns>
protected boolean editIsSEPA()
{
    return CustPaymModeTable.PaymentType == CustPaymentType::BillOfExchange || CustPaymModeTable.PaymentType == CustPaymentType::ElectronicPayment;
}

public void init()
{
    #ISOCountryRegionCodes

    norwayEnabled = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]);

    paymModeTableForm = CustPaymModeTableForm::construct();

    super();

    paymModeTableForm.parmDataObjectBankTransType(custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, BankTransType)));
    paymModeTableForm.parmDataObjectInterCompLedgerDimension(custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, InterCompanyLedgerDimension)));
    paymModeTableForm.parmDataObjectExportOnInvoice(custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, ExportOnInvoice)));
    paymModeTableForm.parmDataObjectAccountType(custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, AccountType)));
    paymModeTableForm.parmCtrlTablePaymValidation(PaymControlTable);
    paymModeTableForm.parmCtrlTablePaymAttributes(PaymAttributeTable);
    paymModeTableForm.formInitPost(custPaymModeTable);

    //This menu item button is exclusive for Norway
    menuItemButton.enabled(norwayEnabled);

    if (FormSecurity::getMenuItemAccessRight(this) == accessRight::View)
    {
        PaymControlTable.enabled(false);
        PaymAttributeTable.enabled(false);
    }

    InterCoAccount.parmPostingType(LedgerPostingType::None);

    PaymentAccount.parmIsDefaultAccount(true);
    // PDC clearing posting field should not be visible when PDC is not selected in GL parameters
    if (BankParameters::find().EnablePDC)
    {
        posting_PDCClearingPosting.visible(true);
    }
}

/// <summary>
///     Gets the electronic payment enum value of <c>CustPaymentType</c>.
/// </summary>
/// <returns>
///     Enum value of electronic payment of enum <c>CustPaymentType</c>.
/// </returns>
protected CustPaymentType getCustPaymentTypeElectronicPayment()
{
    return CustPaymentType::ElectronicPayment;
}

void setFieldAccess()
{
    if (!BrazilParameters::isEnabled())
    {
        return;
    }

    if (custPaymModeTable.ClassId == classNum(CustOutPaym_CNAB_BR))
    {
        custPaymModeTable.PaymentType = element.getCustPaymentTypeElectronicPayment();
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, ExportLayoutGroupId_BR)).allowEdit(true);
    }
    else
    {
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, ExportLayoutGroupId_BR)).allowEdit(false);
        custPaymModeTable.ExportLayoutGroupId_BR = '';
    }

    if (custPaymModeTable.ClassIdReturn == classNum(CustPaymReconcilImport_EP_Return_BR))
    {
        custPaymModeTable.PaymentType = element.getCustPaymentTypeElectronicPayment();
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, ReturnLayoutGroupId_BR)).allowEdit(true);
    }
    else
    {
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, ReturnLayoutGroupId_BR)).allowEdit(false);
        custPaymModeTable.ReturnLayoutGroupId_BR = '';
    }

    if (custPaymModeTable.ClassId == classNum(CustOutPaym_CNAB_BR))
    {
        custPaymModeTAble.PaymOnInvoice = NoYes::No;
        custPaymModeTAble.ExportOnInvoice = NoYes::No;
        custPaymModeTable.PaymJournalNameId = '';
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,PaymOnInvoice)).enabled(false);
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,ExportOnInvoice)).enabled(false);
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,PaymJournalNameId)).enabled(false);
    }
    else
    {
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,PaymOnInvoice)).enabled(true);
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,ExportOnInvoice)).enabled(true);
        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable,PaymJournalNameId)).enabled(true);
    }

    custpaymModeTable_ds.refresh();
}

/// <summary>
/// Enables or disables file format fields.
/// </summary>
/// <param name = "_enableExport">If true, field FileFormats_M_actionClassName is enabled and field FileFormats_ERFormatMappingID is disabled accordingly.</param>
/// <param name = "_enableImport">If true, field FileFormats_M_actionClassNameIn is enabled and field FileFormats_ERModelMappingTable is disabled accordingly.</param>
void enableFileFormatFields(NoYes _enableExport, NoYes _enableImport)
{
    NoYes enableExport = _enableExport;
    NoYes enableImport = _enableImport;
    NoYes enableBR = _enableExport || BrazilParameters::isEnabled();

    FileFormats_ERFormatMappingID.enabled(!enableExport);
    FileFormats_ERModelMappingTable.enabled(!enableImport);
    FileFormats_ExportLayoutGroupId_BR.enabled(enableExport);
    FileFormats_ReturnLayoutGroupId_BR.enabled(enableExport);

    if (this.canEnableExport())
    {
        enableExport = NoYes::Yes;
    }

    FileFormats_M_actionClassName.enabled(enableExport);
    FileFormats_M_actionClassNameIn.enabled(enableImport);
    FileFormats_M_actionClassNameFileAnalyze.enabled(enableExport);

    BankClient_RU_UseCustomFormat_RU.enabled(!enableExport);
    BankClient_RU_bankClientActionClassName_RU.enabled(!enableExport && CustPaymModeTable.UseCustomFormat_RU);

    element.displayExportRefundOption();
}

