public void run()
{
    super();

    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::DigitalCards));
    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::Goods));
    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::Service));
    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::ThirdParty));
    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::Cancellation));
    PaymentType_MX_PaymentType_MX.delete(enum2Str(PaymentType_MX::Blank));
}

public void executeQuery()
{
    if (element.args().record() && element.args().caller().name() == formstr(BankAccountTable))
    {
        BankAccountTable bankAccountTable = element.args().record();
        QueryBuildRange criteriaBankAccountID = custPaymModeTable_ds.query().dataSourceNo(1).addRange(fieldNum(CustPaymModeTable, PaymentLedgerDimension));
        criteriaBankAccountID.value(queryValue(LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(bankAccountTable.AccountID, LedgerJournalACType::Bank)));
    }
    super();
}

