int active()
{
    int ret = super();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.setFieldAccess();

        if (custPaymModeTable.ClassId == classNum(CustOutPaym_CNAB_BR)
            || custPaymModeTable.ClassIdReturn == classNum(CustPaymReconcilImport_EP_Return_BR))
        {
            if (custPaymModeTable.PaymSumBy != PaymSumBy::Invoice)
            {
                ret = checkFailed(strfmt("@GLS670", custPaymModeTable.PaymSumBy, "@SYS25194"));
            }
        }

        custPaymModeTable_ds.object(fieldNum(CustPaymModeTable, PaymOnInvoice)).allowEdit(custPaymModeTable.ClassId != classNum(CustOutPaym_CNAB_BR));

        this.setDescPrimaryMethodAccess_BR();
    }
    // </GBR>

    paymModeTableForm.datasourceActivePost();
    if (norwayEnabled)
    {
        custPaymModeTable_ds.enableFields();
    }

    element.enableFields();

    PaymentFormatCodes_W.visible(PaymentFormatCodeSets_W::isISO20022Country());

    return ret;
}

