public void lookup()
{
    container choices;
    MapEnumerator me = custPaymModeTable.CustVendPaymModeTable::bankClientSupportedFormats_RU().getEnumerator();

    while (me.moveNext())
    {
        choices += me.currentKey();
    }

    FormRun lookupForm = classFactory.createSysLookupPicklist(this);
    lookupForm.init();
    lookupForm.choices(choices);

    this.performFormLookup(lookupForm);
}

