public boolean modified()
{
    boolean ret = super();

    if (this.checked())
    {
        if (paymModeTableForm.checkUnsavedTableItem(this))
        {
            paymModeTableForm.addPaymValidation(PaymControlTable.cell(3, PaymControlTable.row()).data());
        }
    }
    else
    {
        paymModeTableForm.removePaymValidation(PaymControlTable.cell(3, PaymControlTable.row()).data());
    }

    return ret;
}

