/// <summary>
/// Disable the consolidation invoice related controls.
/// </summary>
public void disableConsInvoiceControl_JP()
{
    if (CustConsInvoiceType_JP::isCustConsInvoiceEnabled())
    {
        CustConsInvoice_JP_ConsId.visible(true);
    }
}

public void init()
{
    if (!element.args() || !element.args().caller())
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }

    paymProposalEditForm = element.args().caller();

    super();

    // <GJP>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]))
    {
        element.disableConsInvoiceControl_JP();
    }
    // </GJP>

    paymProposalEditForm.parmFormRun(this);
    paymProposalEditForm.parmCtrlBtnModifyPaymAttributes(btnModifyPaymAttributes);
    paymProposalEditForm.parmOKButton(Ok);
    paymProposalEditForm.formInitPost();

    // <GTH>
    if (TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled())
    {
        this.initTaxBranchControls();
    }
    // </GTH>

    // <GCN>
    // Update design for golden tax integration.
    if (CustParameters::find().TaxIntegrationSystem_CN)
    {
        displayTaxExternalInvoiceId_CN.visible(true);
    }
    // <GCN>

    if (BrazilParameters::isEnabled())
    {
        CustTransOpen_FineAmount_BR.visible(true);
        CustTransOpen_InterestAmount_BR.visible(true);
    }
}

/// <summary>
/// Inits the status of controls that for Thailand Multiple Tax branches feature.
/// </summary>
public void initTaxBranchControls()
{
    // For Thailand MTB controls based on sys fields, they are set as invisiable by default.
    // Needs to init their status here.
    displayTaxBranchDimension_TH.visible(true);
}

public int active()
{
    int ret;

    ret = super();

    // Discount to take is editable when use cash discount is normal and and partial discounts
    // are allowed, or it use cash discount is always.
    if ((custTransOpen.UseCashDisc == UseCashDisc::Always)
            || (custTransOpen.UseCashDisc == UseCashDisc::Normal
                && CustParameters::find().CashDiscForPartialPaym == true))
    {
        specTrans_CashDiscToTake.allowEdit(true);
    }
    else
    {
        specTrans_CashDiscToTake.allowEdit(false);
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        InterestAndFine_BR_InterestAmountPaymCur_BR.label(strfmt("@SYS53547", custVendPaymProposalLine.CurrencyCode));
        InterestAndFine_BR_FineAmountPaymCur_BR.label(strfmt("@GLS50821", custVendPaymProposalLine.CurrencyCode));
        paymProposalEditForm.adjustInterestFineEdition_BR();
    }
    // </GBR>

    return ret;
}

public void modified()
{
    super();

    paymProposalEditForm.fieldModifiedTrans_DueDatePost();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        if (custTransOpen.InterestCode_BR && custTransOpen.FineCode_Br)
        {
            if ((Box::yesNo(strfmt("@GLS1268",
                                    fieldpname(CustTransOpen, DueDate),
                                    fieldpname(CustTransOpen, InterestAmount_BR),
                                    fieldpname(CustTransOpen, FineAmount_BR)),
                                    DialogButton::No) == DialogButton::Yes))
            {
                paymProposalEditForm.fieldModified_FineCodeDueDate_BR();
                paymProposalEditForm.fieldModified_InterestCodeDueDate_BR();
            }
        }
        else
        {
            if (custTransOpen.InterestCode_BR)
            {
                if (Box::yesNo(strfmt("@GLS1009",fieldpname(CustTransOpen, DueDate),
                                fieldpname(CustTransOpen, InterestAmount_BR)), DialogButton::No) == DialogButton::Yes)
                {
                    paymProposalEditForm.fieldModified_InterestCodeDueDate_BR();
                }
            }
            else
            {
                if (custTransOpen.FineCode_BR)
                {
                    if ((Box::yesNo(strfmt("@GLS1009",fieldpname(CustTransOpen, DueDate),
                                    fieldpname(CustTransOpen, FineAmount_BR)), DialogButton::No) == DialogButton::Yes))
                    {
                        paymProposalEditForm.fieldModified_FineCodeDueDate_BR();
                    }
                }
            }
        }
    }
    // </GBR>
}

