        public boolean validateDelete()
        {
            boolean ret = true;

            if (!paymProposalEditForm.parmSkipUserInteractionDuringSpecTransDelete())
            {
                ret = paymProposalEditForm.promptUserToProceedWithDelete();
            }

	    // <GBR>
            if (BrazilParameters::isEnabled())
            {
                // Clear Interest/Fine Amount information
                CustVendOpenTransManager::clearInterestAndFineAmounts_BR(specTrans.SpecCompany, specTrans.SpecTableId, specTrans.SpecRecId);
            }
            // </GBR>

            return ret;
        }

public edit PaymAmountCur paymAmountPaymCur(
    boolean _set,
    SpecTrans _specTrans,
    AmountCur _paymAmountCur)
{
    AmountCur amount;

    if (_specTrans)
    {
        amount = paymProposalEditForm.editPaymAmountPaymCur(_set, _specTrans, _paymAmountCur);
    }
    else
    {
        amount = 0;
    }

    return amount;
}

public boolean validate()
{
    boolean valid;
    CustVendAC newAccountNum;

    // invoke base class first to check if the company Id is fundamentally valid
    valid = super();

    if (valid)
    {
        newAccountNum = paymProposalEditForm.getPaymCustVendForSelectedCompany();

        if (newAccountNum != '')
        {
            custVendPaymProposalLine.AccountNum = newAccountNum;
        }
        else
        {
            // Failed to retrieve a mapped vendor in the selected company
            valid = checkFailed("@SYS116457");
        }
    }

    return valid;
}

public void performFormLookup(xFormRun _form, FormControl _formControl)
{
    if (isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        // centralized payments is enabled - modify the account number company lookup to limit the companies listed
        // to those that are valid selections for the current proposal line
        paymProposalEditForm.modifyPaymProposalAccounNumCompanyLookup(_form);
    }

    super(_form, _formControl);
}

