void enableFacturePrint_RU()
{
    Query query;
    QueryRun queryRun;

    if (custPostInvoiceJob.showFacture_RU())
    {
        printFacture_RU.enabled(qbrPosted.value() == queryValue(NoYes::Yes) || ! updateNow);

        if (! printFacture_RU.enabled())
        {
            query = new Query(custPostInvoiceJob.queryRun().query());
            findOrCreateRange_W(query.dataSourceTable(tablenum(CustInvoiceTable)),
                                fieldnum(CustInvoiceTable, CorrectedFactureExternalId_RU),
                                SysQuery::valueNotEmptyString());

            queryRun = new QueryRun(query);
            if (! queryRun.next())
            {
                printFacture_RU.enabled(true);
            }
        }
    }
}

public void init()
{
    isBillingClassificationEnabled = CustParameters::isBillingClassificationEnabled();

    // Make sure that we were called with an instance of an object passed in
    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    // Get the instance of the runBase class we were called from
    custPostInvoiceJob = element.args().caller().runbase();

    super();

    if (isBillingClassificationEnabled)
    {
        billingClassificationNames.label("@SPS371");
        billingClassificationMultiSelectCtrl = SysLookupMultiSelectCtrl::construct(element, billingClassificationNames, queryStr(CustBillingClassificationLookup));

        billingClassificationMultiSelectCtrl.set(CustBillingClassification::custBillingClassificationForMultiselectControl(custPostInvoiceJob.parmBillingClassification()));
    }
    billingClassificationNames.visible(isBillingClassificationEnabled);

    // Default the form's fields from the last RunBase values
    updateNow = custPostInvoiceJob.parmProforma();
    printOutWhen.selection(custPostInvoiceJob.parmPrintOutWhen());
    printOut.value(custPostInvoiceJob.parmPrintOut());
    usePrintManagement.value(custPostInvoiceJob.parmUsePrintManagement());

    // default correction checked if the invoice is negative and the parameter is set
    creditError.value(custPostInvoiceJob.parmInvoiceTotal() < 0 && CustParameters::find().CreditError);
    custCreditMaxCheck.selection(custPostInvoiceJob.parmCustCreditMaxCheck());

    // <GEERU>
    qbrPosted = SysQuery::findOrCreateRange(custPostInvoiceJob.queryRun().query().dataSourceTable(tablenum(CustInvoiceTable)), fieldnum(CustInvoiceTable,Posted));

    element.updateDesign_W();

    if (UseOriginalDocumentAsFacture_RU.visible())
    {
        QueryBuildRange qbrInvoiceAccount = custPostInvoiceJob.queryRun().query().dataSourceTable(tablenum(CustInvoiceTable)).findRange(fieldnum(CustInvoiceTable, InvoiceAccount)); 

        if (qbrInvoiceAccount)
        {
            UseOriginalDocumentAsFacture_RU.value(CustTable::find(qbrInvoiceAccount.value()).UseOriginalDocumentAsFacture_RU);

        }
    }

    // </GEERU>
    // <GEEU>
    advanceInvoiceSettlement = custPostInvoiceJob.parmAdvanceInvoiceSettlement_W();
    // </GEEU>

    // Enable/disable appropriate fields
    element.updateDesign();

    // <GTH>
    if (TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
    {
        this.updateDesignForUnrealizedVAT();
    }
    // </GTH>

    custPostInvoiceJob.dialogUpdateQueryDelegate += eventhandler(this.updatePrintSettingsButtonsOnDialogQueryUpdated);
}

RunBase runBase()
{
    return custPostInvoiceJob;
}

public void updateDesign()
{
    #ISOCountryRegionCodes

    // Determine if the UsePrintManagement checkbox should be enabled; clear it if disabled
    // <GEERU>
    usePrintManagement.enabled(printOut.value() || printFacture_RU.value());
    // </GEERU>
    if (!usePrintManagement.enabled())
    {
        usePrintManagement.value(enum2int(NoYes::No));
    }

    //<GMX>
    this.updateDesign_MX();
    //</GMX>

   // <GBR>
    if (BrazilParameters::isEnabled())
    {
        if (!custPostInvoiceJob.hasFixedAsset_BR())
        {
            TabPageBillOfLanding_BR.visible(false);
            BillOfLading_FreightChargeTerms.selection(WMSFreightChargeTerms::Nofreight);
        }
    }
    // </GBR>
}

public void updateDesign_MX()
{
    if (EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled())
    {
        PrintOut.value(false);
        Printout.enabled(false);
    }
}

#ISOCountryRegionCodes
public void updateDesign_W()
{
    boolean showCODCashReceipt_W;
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        printAcceptanceReport_RU.visible(true);
        printInMST_RU.visible(true);
        printAcceptanceReport_RU.value(custPostInvoiceJob.parmPrintAcceptanceReport_RU());
        printInMST_RU.value(custPostInvoiceJob.parmPrintInMST_RU());

        if (custPostInvoiceJob.showFacture_RU())
        {
            printFacture_RU.visible(true);
            element.enableFacturePrint_RU();
            printFacture_RU.value(custPostInvoiceJob.parmPrintFacture_RU() && printFacture_RU.enabled());
            UseOriginalDocumentAsFacture_RU.visible(true);
        }
        else
        {
            printFacture_RU.visible(false);
            printFacture_RU.value(false);
            UseOriginalDocumentAsFacture_RU.visible(false);
        }
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        plFiscalDocType.selection(custPostInvoiceJob.parmFiscalDocType_PL());
    }

    // <GEEU>
    tabAdvanceInvoices_W.visible(isAdvanceInvoiceEnabled);
    // </GEEU>
    showCODCashReceipt_W = custPostInvoiceJob.showCODCashReceipt_W();
    printCODCashReceipt_W.visible(showCODCashReceipt_W);
    tabCashRegister_W.visible(showCODCashReceipt_W);
    element.enableCODCashReceipt_W();
}

/// <summary>
///     Sets fields visible.
/// </summary>
public void updateDesignForUnrealizedVAT()
{
    printDocuSet.visible(true);
    documentSet.visible(true);
    if (PrintDocuSet.value())
    {
        DocumentSet.allowEdit(true);
    }
}

edit boolean updateNow(boolean _set, boolean _updateNow)
{
    // Reverse the Proform/Posting checkbox
    if (_set)
    {
        updateNow = ! _updateNow;

        // <GEERU>
        element.enableFacturePrint_RU();
        if (! printFacture_RU.enabled())
        {
            printFacture_RU.value(false);
        }
        // </GEERU>
        element.enableCODCashReceipt_W();
    }

    return ! updateNow;
}

public void enableCODCashReceipt_W()
{
    CashRegisterTerminal_W cashRegisterTerminal;
    CashRegisterOperator_W cashRegisterOperator;

    boolean enableCashRegister;

    if (printCODCashReceipt_W.visible())
    {
        enableCashRegister = !updateNow && custPostInvoiceJob.allowEditCODCashReceipt_W();

        if (printCODCashReceipt_W.allowEdit(enableCashRegister))
        {
            printCODCashReceipt_W.value(custPostInvoiceJob.parmPrintCODCashReceipt());
        }
        else
        {
            printCODCashReceipt_W.value(false);
        }

        boolean isCashRegisterSectionEmpty = true;
        if (TerminalRecId.allowEdit(enableCashRegister))
        {
            if (!TerminalRecId.value())
            {
                cashRegisterOperator = CashRegisterOperator_W::currentOperator();

                if (cashRegisterOperator)
                {
                    isCashRegisterSectionEmpty = false;
                    cashRegisterTerminal = cashRegisterOperator.defaultTerminal();
                    LocationRecId.value(cashRegisterOperator.Location);
                    TerminalRecId.value(cashRegisterTerminal.RecId);
                    RegisterRecId.value(cashRegisterTerminal.CashRegister);
                    OperatorRecId.value(cashRegisterOperator.RecId);
                }
            }
        }

        if (isCashRegisterSectionEmpty)
        {
            LocationRecId.value(0);
            TerminalRecId.value(0);
            RegisterRecId.value(0);
            OperatorRecId.value(0);
        }
    }
}

