public boolean modified()
{
    boolean ret;

    ret = super();

    if (PrintDocuSet.value())
    {
        DocumentSet.allowEdit(true);
    }
    else
    {
        DocumentSet.allowEdit(false);
    }

    return ret;
}

public Common lookupReference()
{
    return CashRegisterTerminal_W::lookupTerminalForLocation(this, LocationRecId.value());
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        RegisterRecId.value(CashRegisterTerminal_W::find(this.value()).CashRegister);
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (this.value() && CashRegisterTerminal_W::find(this.value()).Location != LocationRecId.value())
        {
            ret = checkFailed(strFmt("@FiscalServices:AS_CustPostInvoiceJob_Form_TerminalRecId_Method_Validate", CashRegisterLocation_W::find(LocationRecId.value()).Code));
        }
    }

    return ret;
}
