[Form]
public class CustPosting extends FormRun implements OfficeIMenuCustomizer, OfficeITemplateCustomExporter
{
    FormStringControl lineAccountField;

    #isoCountryRegionCodes

    const str custPostingProfileOfficeMenuId = 'CustCustomerPostingProfileTemplateId';
}

void init()
{
    super();

    lineAccountField = CustLedgerAccounts_NumHeader;

    custLedgerAccounts_SumAccount.parmPostingType(LedgerPostingType::CustBalance);

    custLedgerAccounts_ClearingAccount.parmPostingType(LedgerPostingType::CustSettlement);

    custLedgerAccounts_VATprepaymentsAccount.parmPostingType(LedgerPostingType::Tax);

    custLedgerAccounts_LiabilitiesForDiscountAccount.parmPostingType(LedgerPostingType::None);

    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(LedgerBasicSalesTax)))
        {
            custLedgerAccounts_ExportSalesLedgerDimension.parmPostingType(LedgerPostingType::None);
        }

        CustLedgerAccounts_DepositLedgerDimension.parmPostingType(LedgerPostingType::None);
    }
    // </GEEPL>
}

void setLookupButton()
{
    lineAccountField.allowEdit(true);
    lineAccountField.skip(false);

    if (custLedgerAccounts.AccountCode== TableGroupAll::All)
    {
        lineAccountField.allowEdit(false);
        lineAccountField.skip(true);
        custLedgerAccounts.Num = '';
    }
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    var postingProfileHeader = _menuOptions.getOptionsForEntity(tableStr(CustCustomerPostingProfileHeaderEntity));

    if (postingProfileHeader)
    {
        postingProfileHeader.includeDefault(false);
    }

    var postingProfileMenuItem = OfficeTemplateExportMenuItem::construct(OfficeAppApplicationType::Excel, resourceStr(CustCustomerPostingProfileTemplate), custPostingProfileOfficeMenuId);
    postingProfileMenuItem.displayName("@AccountsReceivable:CustPostingOpenProfileInExcel");

    _menuOptions.customMenuItems().addEnd(postingProfileMenuItem);
}

Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);
    if (_menuItem.id() == custPostingProfileOfficeMenuId)
    {
        var lineEntityName = tableStr(CustCustomerPostingProfileLineEntity);
        var headerEntityName = tableStr(CustCustomerPostingProfileHeaderEntity);

        // Create lines filter
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);

        var lineFilter = lineFilterBuilder.and(
                                            lineFilterBuilder.areEqual(fieldStr(CustCustomerPostingProfileLineEntity, PostingProfile), custLedger.PostingProfile),
                                            lineFilterBuilder.areEqual(fieldStr(CustCustomerPostingProfileLineEntity, DataAreaId), custLedger.dataAreaId));

        filtersToApply.insert(lineEntityName, lineFilter);

        // Create header filter
        ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

        var headerFilter = headerFilterBuilder.and(
                                            headerFilterBuilder.areEqual(fieldStr(CustCustomerPostingProfileHeaderEntity, PostingProfile), custLedger.PostingProfile),
                                            headerFilterBuilder.areEqual(fieldStr(CustCustomerPostingProfileHeaderEntity, DataAreaId), custLedger.dataAreaId));

        filtersToApply.insert(headerEntityName, headerFilter);

        return filtersToApply;
    }
    return filtersToApply;
}

void updateTemplateSettings(OfficeTemplateExportMenuItem _menuItem, Microsoft.Dynamics.Platform.Integration.Office.SettingsManager _settingsManager)
{
    //Required by interface not important to this senario
}

int active()
{
    int ret;

    ret = super();
    element.setLookupButton();

    return ret;
}

