    if (CustLedger_DS.cursor().RecId != 0)
    {
        // A header must exist before the line can be created.
        super();
    }
}

public void clicked()
{
    if (CustLedger_DS.cursor().RecId != 0)
    {
        // A header must exist before the line can be removed.
        super();
    }
}
