[Form]
public class CustPrepaymentApplication extends FormRun
{
    CustPrepaymentCustInvoice custPrepaymentCustInvoice;
    CustPrepaymentInvoice custPrepaymentInvoice; //class supporting business logic for prepayment application and its reversal
    boolean isPrepaymentApplication; //flag to identify if the form is for prepayment application or reversal
    AccountNum invoiceAccount;
    Common callerTable;
    boolean isInvoicePosted;
    SalesTotals salesTotals;

}

public void toggleAllowEditFields()
{
    SalesTable salesTableLoc = element.args().record() as SalesTable;
    CustPrepaymentCustInvoice custPrepaymentCustInvoiceLoc = CustPrepaymentCustInvoice::findByRecIdPrepaymentInvoiceId(salesTableLoc.RecId, tmpSalesPrepaymentInvoice.InvoiceRecId, salesTableLoc.TableId);

    tmpSalesPrepaymentInvoice_AppliedAmount.allowEdit(custPrepaymentCustInvoiceLoc?true:false);
}

/// <summary>
/// Called when the form is initialized.This will initialize the data source 
/// <c>tmpSalesPrepaymentInvoice</c> with the prepayment invoice details applicable for the sales order.
/// </summary>
public void init()
{
    super();

    if (!(element.args().caller() || element.args().record()))
    {
        throw error("@SYS22539");
    }

    if (element.args().caller() is FormRun)
    {
        callerTable = element.args().record();
        isPrepaymentApplication = true;
        custPrepaymentInvoice = CustPrepaymentInvoice::construct(isPrepaymentApplication);

        switch (callerTable.TableId)
        { 
            case tableNum(SalesTable):
                isInvoicePosted = false;
                SalesTable salesTableLoc = element.args().record() as SalesTable;
                tmpSalesPrepaymentInvoice.setTmpData(custPrepaymentInvoice.populateSalesOrderApplicablePrepaymentInvoice(salesTableLoc));
                salesTotals = SalesTotals_Sales::construct(salesTableLoc,
                                               SalesUpdate::All,
                                               AccountOrder::None,
                                               '',
                                               salesTableLoc.SalesId);
                salesTotals.calc();
                break;
        }
        element.toggleAllowEditFields();

        if (!isPrepaymentApplication)
        {
            Tab.caption('@AccountsPayable:VendorPrepaymentsReverse');
        }
    }
    else
    {
        throw error(strFmt("@SYS105865","@SYS183647","@SYS8008"));
    }
}

public int active()
{
    int ret;

    ret = super();

    SalesTable selectedSalesTable = this.cursor();
    tmpSalesPrepaymentInvoice.setTmpData(custPrepaymentInvoice.populateSalesOrderApplicablePrepaymentInvoice(selectedSalesTable));
    element.toggleAllowEditFields();
    tmpSalesPrepaymentInvoice_ds.research(true);

    return ret;
}

public display AmountCur TotalAmount()
{
    return salesTotals.TotalAmount();
}

public boolean validateTotalAppliedAmount(AmountCur _amountApplied)
{
    boolean ret = true;

    if (SalesTable_ds.applyAmountSum() + _amountApplied > SalesTable_ds.TotalAmount())
    {
        ret = false;
    }

    return ret;
}

/// <summary>
/// Mark the prepayment invoice against the sales order and update the applied amount.
/// </summary>
/// <param name="_set">
/// Value to be set
/// </param>
/// <param name="_tmpSalesPrepaymentInvoice">
/// The prepayment invoice details
/// </param>
/// <param name="_markTrans">
/// Is the value marked
/// </param>
/// <returns>
/// The value of the markTrans field
/// </returns>
public edit NoYes markTrans(boolean _set, tmpSalesPrepaymentInvoice _tmpSalesPrepaymentInvoice, NoYes _markTrans)
{
    NoYes returnMarkTrans;
    SalesTable salesTableLoc = element.args().record() as salesTable;

    if (_set)
    {
        // Validate if there are no entries already
        CustPrepaymentCustInvoice custPrepaymentCustInvoiceLoc = CustPrepaymentCustInvoice::findByRecIdPrepaymentInvoiceId(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, salesTableLoc.TableId);

        if (custPrepaymentCustInvoiceLoc && _markTrans)
        {
            throw error("@AccountsReceivable:PrepaymentAlreadyMarked");
        }

        custPrepaymentInvoice.updateTransMarked(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, _markTrans, salesTableLoc.TableId, _tmpSalesPrepaymentInvoice.InvoiceDate );

        if (_markTrans)
        {
            CustPrepaymentCustInvoice custPrepaymentCustInvoiceCheck = CustPrepaymentCustInvoice::findByRecIdPrepaymentInvoiceId(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, salesTableLoc.TableId);

            AmountCur AppliedAmount = _tmpSalesPrepaymentInvoice.AppliedAmount;
            AmountCur AdvanceAmount = _tmpSalesPrepaymentInvoice.AdvanceAmount;

            if (AppliedAmount == 0)
            {
                AppliedAmount = AdvanceAmount;
            }

            if (!SalesTable_ds.validateTotalAppliedAmount(AppliedAmount))
            {
                throw warning("@SYS331457");
            }

            //sets advance apply amount to maximum possible value when selected a paid advance
            if (AppliedAmount > 0 && AppliedAmount <= AdvanceAmount &&  AppliedAmount <= _tmpSalesPrepaymentInvoice.InvoiceAmount)
            {
                custPrepaymentInvoice.updateAdvanceApplicationAmount(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, AppliedAmount, salesTableLoc.TableId);
            }

        }

        element.toggleAllowEditFields();
        SalesTable_ds.research(true);
        tmpSalesPrepaymentInvoice_ds.research(true);

    }

    CustPrepaymentCustInvoice custPrepaymentCustInvoiceLoc = CustPrepaymentCustInvoice::findByRecIdPrepaymentInvoiceId(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, salesTableLoc.TableId);

    returnMarkTrans = (custPrepaymentCustInvoiceLoc)?NoYes::Yes:NoYes::No;
    return returnMarkTrans;
}

private void checkExistingPrepaymentTransInOpenPeriod( CustPrepaymentCustInvoice _custPrepaymentCustInvoice)
{
   RecId calendarRecId = CompanyInfoHelper::fiscalCalendarRecId();

   if (!LedgerFiscalCalendar::checkDateIsValid(calendarRecId, _custPrepaymentCustInvoice.SettlementPostingDate, FiscalPeriodType::Operating, false))
   {
        throw warning("@AccountsPayable:MarkedPrepaymentInClosedPeriod");
   }

}

public edit AmountCur updateApplicationAmount(
    boolean _set,
    tmpSalesPrepaymentInvoice _tmpSalesPrepaymentInvoice,
    AmountCur _appliedAmount)
{
    SalesTable salesTableLoc = element.args().record() as salesTable;
    AmountCur applicationAmount = 0;

    if (_set)
    {
        if (_appliedAmount > 0 && _appliedAmount <= _tmpSalesPrepaymentInvoice.AdvanceAmount)
        {
            if (SalesTable_ds.validateTotalAppliedAmount(_appliedAmount))
            {
                _tmpSalesPrepaymentInvoice.AppliedAmount = _appliedAmount;
                _tmpSalesPrepaymentInvoice.AdvanceRemaining = _tmpSalesPrepaymentInvoice.AdvanceAmount - _appliedAmount;
                custPrepaymentInvoice.updateAdvanceApplicationAmount(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, _appliedAmount, salesTableLoc.TableId);
                SalesTable_ds.research(true);
                tmpSalesPrepaymentInvoice_ds.research(true);
            }
            else
            {
                throw warning("@SYS331457");
            }
        }
        else
        {
            custPrepaymentInvoice.updateTransMarked(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, NoYes::No, salesTableLoc.TableId, _tmpSalesPrepaymentInvoice.InvoiceDate);
            throw warning("@SYS342088"); //throws an error message when prepayment amount being applied is zero
        }
    }

    CustPrepaymentCustInvoice custPrepaymentCustInvoiceLoc = CustPrepaymentCustInvoice::findByRecIdPrepaymentInvoiceId(salesTableLoc.RecId, _tmpSalesPrepaymentInvoice.InvoiceRecId, salesTableLoc.TableId);

    if (custPrepaymentCustInvoiceLoc)
    {
        applicationAmount = custPrepaymentCustInvoiceLoc.AmountCur;
    }

    return applicationAmount;
}
