[Form]
public class CustPrepaymentFactureCreate_RU extends FormRun
{
    CustTransDetails    custTransDetails;
    Map                 markTransMap;
    CommProfileType_RU  commProfileType;
    RecordSortedList    rslCustTrans;
    Map                 custUseOriginalDocumentAsFactureMap;

}

	void createFacture()
    {
        CustPrepaymentFactureCreate_RU::newFromForm(markTransMap.pack(), custTrans, rslCustTrans);
        element.initMarkSet();

        if (commProfileType == CommProfileType_RU::CommissionAgent ||
			commProfileType == CommProfileType_RU::CommissionPrincipalAgent)
        {
            element.initCommTransTmp();
        }

        custTrans_DS.research();
    }

public UseOriginalDocumentAsFacture_RU getUseOriginalDocumentAsFacture(CustAccount _custAccount)
{
    UseOriginalDocumentAsFacture_RU useOriginalDocumentAsFacture;
    if (custUseOriginalDocumentAsFactureMap.exists(_custAccount))
    {
        useOriginalDocumentAsFacture =  custUseOriginalDocumentAsFactureMap.lookup(_custAccount);
    }
    else
    {
        useOriginalDocumentAsFacture = CustTable::find(_custAccount).UseOriginalDocumentAsFacture_RU;
        custUseOriginalDocumentAsFactureMap.insert(_custAccount, useOriginalDocumentAsFacture);
    }
    return useOriginalDocumentAsFacture;
}

	public void init()
    {
        this.initMarkSet();

        custUseOriginalDocumentAsFactureMap = new Map(Types::String, Types::Enum);

        if (element.args().parmEnumType() == enumNum(CommProfileType_RU))
        {
            commProfileType = element.args().parmEnum();
        }

        super();

        paymentTab.visible(commProfileType == CommProfileType_RU::General);
    }

    void initCommTransTmp()
    {
        CommReportJour_RU            commReportJour;
        CommReportTransPrepayment_RU commReportTrans;
        FactureJour_RU               factureJour;
        VendTrans                    vendTrans;
        MapEnumerator                enumerator;
        Map                          mapVend   = new Map(Types::Integer, Types::String);
        Map                          mapCust   = MappingsInfo_RU::createMapWithFieldId(tableNum(CustTrans), tableNum(CustVendTrans));
        DictTable                    dictTable = new DictTable(tableNum(VendTrans));
        fieldId                      fieldId;

        enumerator = MappingsInfo_RU::createMapWithFieldId(tableNum(VendTrans), tableNum(CustVendTrans)).getEnumerator();
        while (enumerator.moveNext())
        {
            mapVend.insert(enumerator.currentValue(), enumerator.currentKey());
        }

        custTrans.setTmp();
        custTrans_W.setTmp();

        rslCustTrans = new RecordSortedList(tableNum(CustTrans));
        rslCustTrans.sortOrder(fieldNum(CustTrans, ReasonRefRecId));

        delete_from custTrans;
        delete_from custTrans_W;

        while select commReportTrans
	        where commReportTrans.Approved == NoYes::Yes
		join commReportJour
			where commReportJour.ReportId      == commReportTrans.ReportId
           && commReportJour.PartnerType   == ModuleCustVend::Cust
		notexists join factureJour
			where factureJour.RefCommReportTrans == commReportTrans.RecId
        {
            custTrans.clear();
            vendTrans = commReportTrans.custVendTrans();

            fieldId = dictTable.fieldNext(0);

            while (fieldId && ! isSysId(fieldId))
            {
                if (mapVend.exists(fieldId) && mapCust.exists(mapVend.lookup(fieldId)))
                {
                    custTrans.(mapCust.lookup(mapVend.lookup(fieldId))) = vendTrans.(fieldId);
                }
                fieldId = dictTable.fieldNext(fieldId);
            }

            custTrans.AccountNum            = commReportJour.PartnerCode;
            custTrans.DefaultDimension      = AgreementHeaderExt_RU::createAgreementDimension(commReportJour.agreementHeaderExt().RecId,
                                                                                          custTrans.DefaultDimension);
            custTrans.ReasonRefRecId        = commReportTrans.RecId;
            custTrans.DocumentNum           = commReportTrans.DocumentNum;
            custTrans.DocumentDate          = commReportTrans.DocumentDate;

            custTrans.AmountCur             = -CurrencyExchangeHelper::amount(custTrans.AmountCur * (custTrans.AmountMST ?
                                                                                   commReportTrans.LineAmountMST / custTrans.AmountMST : 0),
                                                                                   custTrans.CurrencyCode);
            custTrans.AmountMST             = -commReportTrans.LineAmountMST;

            custTrans.doInsert();

            custTrans_W.clear();
            custTrans_W.CustTrans             = custTrans.RecId;
            custTrans_W.CommProfileType_RU    = vendTrans.vendTrans_W().CommProfileType_RU;
            custTrans_W.PrepaymentFactureId_W = vendTrans.vendTrans_W().PrepaymentFactureId_W;
            custTrans_W.doInsert();

            custTrans.packCustTrans_W(custTrans_W.data());
            rslCustTrans.ins(CustTrans);
        }
    }

void initMarkSet()
{
    markTransMap = new Map(Types::Container, Types::Enum);
}

void markUnmarkLines(NoYes _mark)
{
    QueryRun    qr = new QueryRun(custTrans_QR.query().pack());
    CustTrans   custTransMark, currentTrans;

    currentTrans.data(custTrans);

    if(_mark == NoYes::Yes)
    {
        if (custTrans.isTmp())
        {
            custTransMark.setTmp();
            custTransMark.setTmpData(custTrans);

            while select TransDate, AccountNum from custTransMark
            {
                markTransMap.insert([custTransMark.TransDate, custTransMark.RecId], this.getUseOriginalDocumentAsFacture(custTransMark.AccountNum));
            }
        }
        else
        {
            while(qr.next())
            {
                custTransMark = qr.get(tableNum(CustTrans));
                markTransMap.insert([custTransMark.TransDate, custTransMark.RecId], this.getUseOriginalDocumentAsFacture(custTransMark.AccountNum));
            }
        }
    }
    else
    {
        element.initMarkSet();
    }

    custTrans_DS.research();
    custTrans_DS.positionToRecord(currentTrans);
    custTrans_DS.reread();
}

public int active()
{
    int ret;

    ret = super();
    custTransDetails.setCustVendTrans(custTrans);

    return ret;
}

        edit NoYes markTrans(boolean    _set,
							 CustTrans  _custTrans,
							 NoYes      _markTrans)
        {
            NoYes   res;
            if(_set)
            {
                if (_markTrans == NoYes::Yes)
                {
                    markTransMap.insert([_custTrans.TransDate, _custTrans.RecId], element.getUseOriginalDocumentAsFacture(_custTrans.AccountNum));
                    res = true;
                }
                else
                {
                    markTransMap.remove([_custTrans.TransDate, _custTrans.RecId]);
                    res = false;
                }
            }
            else
            {
                res = markTransMap.exists([_custTrans.TransDate, _custTrans.RecId]);
            }
            return res;
        }

        public void init()
        {
            QueryBuildDataSource    qbds;
            QueryBuildRange         qbr;

            super();

            if (commProfileType == CommProfileType_RU::CommissionAgent ||
				commProfileType == CommProfileType_RU::CommissionPrincipalAgent)
            {
                this.query().dataSourceTable(tableNum(CustTrans)).clearDynalinks();
                element.initCommTransTmp();
            }
            else
            {
                qbds = this.query().dataSourceTable(tableNum(CustTrans));
                qbr  = findOrCreateRange_W(qbds, fieldNum(CustTrans, Prepayment), queryValue(NoYes::Yes));
                qbr.status(RangeStatus::Hidden);
                qbr  = findOrCreateRange_W(qbds, fieldNum(CustTrans, Correct), queryValue(NoYes::No));
                qbr.status(RangeStatus::Hidden);
                qbr = findOrCreateRange_W(qbds, fieldNum(CustTrans, TransType),SysQuery::valueNot(LedgerTransType::ExchAdjustment));
                qbr.status(RangeStatus::Hidden);
                qbr = findOrCreateRange_W(qbds, fieldNum(CustTrans, Closed), queryValue(dateNull()));
                qbr.status(RangeStatus::Hidden);
                qbds.addSortField(fieldNum(CustTrans, TransDate));
            }

            custTransDetails = new CustTransDetails(custTrans);
        }

        edit NoYes markUseOriginalDocumentAsFacture(boolean    _set,
									CustTrans  _custTrans,
									NoYes      _markTrans)
        {
            NoYes   res;
            if(_set)
            {
                if(markTransMap.exists([_custTrans.TransDate, _custTrans.RecId]))
                {
                    markTransMap.insert([_custTrans.TransDate, _custTrans.RecId], _markTrans);
                    res = _markTrans;
                }
                else
                {
                    res = element.getUseOriginalDocumentAsFacture(_custTrans.AccountNum);
                }
            }
            else
            {
                if(markTransMap.exists([_custTrans.TransDate, _custTrans.RecId]))
                {
                    res = markTransMap.lookup([_custTrans.TransDate, _custTrans.RecId]);
                }
                else
                {
                    res = element.getUseOriginalDocumentAsFacture(_custTrans.AccountNum);
                }
            }
            return res;
        }

        public void init()
        {
            QueryBuildDataSource    qbds;
            QueryFilter             qf;

            super();

            if (commProfileType != CommProfileType_RU::CommissionAgent &&
				commProfileType != CommProfileType_RU::CommissionPrincipalAgent)
            {
                qbds = this.query().dataSourceTable(tableNum(CustTrans_W));
                qf = this.query().addQueryFilter(qbds, fieldStr(CustTrans_W, PrepaymentFactureId_W));
                qf.value(SysQuery::valueEmptyString());
                qf.status(RangeStatus::Hidden);
            }
        }
