[Form]
public class CustPrepaymentInvoice extends FormRun
{
    tableId callerTableId;

}

public void init()
{
    SalesTable localSalesTable;

    super();

    element.dataHelper().RecordDeleted += eventhandler(this.postRecordDeleted);

    if (element.args().record())
    {
        callerTableId = element.args().record().TableId;
    }

    if (callerTableId == tableNum(SalesTable))
    {
        localSalesTable = element.args().record();

        if (!CustPrepaymentInvoiceTable::existsSalesId(localSalesTable.SalesId))
        {
            CustPrepaymentInvoiceTable_ds.create();
        }
    }

    CustPrepaymentInvoiceTable_ds.query().dataSourceTable(tableNum(CustPrepaymentInvoiceTable)).addRange(fieldNum(CustPrepaymentInvoiceTable, Type)).value(enum2str(PrepaymentInvoiceType::Common));
    element.updateDesign();
}

public void updateDesign()
{
    if (CustPrepaymentInvoiceTable.Posted == NoYes::Yes)
    {
        ButtonCustPostAdvanceInvoice.enabled(false);
        LedgerTransVoucherButton.enabled(true);
        Journals.enabled(true);
        CustPrepaymentInvoiceTable_ds.allowCreate(false);
        custPrepaymentInvoiceTable_ds.allowDelete(false);
        PrintInvoice.enabled(true);
        TabMain.enabled(false);
    }
    else
    {
        ButtonCustPostAdvanceInvoice.enabled(true);
        LedgerTransVoucherButton.enabled(false);
        Journals.enabled(false);
        CustPrepaymentInvoiceTable_ds.allowCreate(true);
        custPrepaymentInvoiceTable_ds.allowDelete(true);
        PrintInvoice.enabled(false);
        TabMain.enabled(true);
    }

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Sales))
    {
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxGroup)).allowEdit(false);
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxItemGroup)).allowEdit(false);
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, OverrideSalesTax)).allowEdit(true);
    }
    else
    {
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxGroup)).allowEdit(true);
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxItemGroup)).allowEdit(true);
        CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, OverrideSalesTax)).allowEdit(false);
    }
}

void create(boolean append = false)
{
    SalesTable localSalesTable;

    custPrepaymentInvoiceTable_ds.allowEdit(true);
    custPrepaymentInvoiceTable_ds.allowCreate(true);
    custPrepaymentInvoiceTable_ds.allowDelete(true);

    custPrepaymentInvoiceTable_ds.object(fieldnum(CustPrepaymentInvoiceTable, OneTimeCustomer)).skip(false);
    custPrepaymentInvoiceTable_ds.object(fieldnum(CustPrepaymentInvoiceTable, OneTimeCustomer)).allowEdit(true);

    super();

    if (callerTableId)
    {
        switch (callerTableId)
        {
            case tablenum(SalesTable):
                if (CustPrepaymentInvoiceCreate::createFromForm(element.args().record(),
                                                       custPrepaymentInvoiceTable,
                                                       custPrepaymentInvoiceLine))
                {
                    custPrepaymentInvoiceLine_ds.research();
                    custPrepaymentInvoiceLine_ds.active();
                    custPrepaymentInvoiceTable_ds.rereadReferenceDataSources();
                }
                else
                {
                    localSalesTable = element.args().record();

                    if (!CustPrepaymentInvoiceTable::existsSalesId(localSalesTable.SalesId))
                    {
                        element.close();
                    }
                }
        }
    }

}

public int active()
{
    int ret;
    ret = super();
    element.updateDesign();
    return ret;
}

display TaxAmount VATAmount()
{
    return custPrepaymentInvoiceLine.LineAmount - this.VATBaseAmount();
}

display TaxBaseCur VATBaseAmount()
{
    TaxModuleType taxModuleType = TaxModuleType::SalesInvoice;
    TaxBaseCur taxbaseAmount;

    if (!custPrepaymentInvoiceTable)
    {
        return 0.0;
    }

    if (custPrepaymentInvoiceTable.SalesId)
    {
        taxModuleType = SalesTable::find(custPrepaymentInvoiceTable.SalesId).taxModuleType();
    }

    // Call the TaxIntegration service to get the tax base amount
    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Sales))
    {
        taxbaseAmount = CustPrepaymentInvoiceLine::calcUsingTaxIntegration(custPrepaymentInvoiceTable, custPrepaymentInvoiceLine);
        return taxbaseAmount; 
    }

    return Tax::baseAmountExclTax(custPrepaymentInvoiceLine.TaxGroup, custPrepaymentInvoiceLine.TaxItemGroup, custPrepaymentInvoiceTable.InvoiceDate, custPrepaymentInvoiceTable.CurrencyCode, custPrepaymentInvoiceLine.LineAmount, taxModuleType);
}

public void modified()
{
    super();
    CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxGroup)).allowEdit(custPrepaymentInvoiceLine.OverrideSalesTax == NoYes::Yes);
    CustPrepaymentInvoiceLine_ds.object(fieldNum(CustPrepaymentInvoiceLine, TaxItemGroup)).allowEdit(custPrepaymentInvoiceLine.OverrideSalesTax == NoYes::Yes);
}

/// <summary>
///On click of the button, print the invoice using GER Configuration
/// </summary>
public void clicked()
{
    CustPrepaymentInvoiceTable custPrepaymentInvoiceTableLoc = custPrepaymentInvoiceTable_ds.cursor();
    CustPrepaymentInvoice::generateGERReport(custPrepaymentInvoiceTableLoc);
}

