[Form]
public final class CustQuotationConfirmationJournal extends FormRun
{
    InventDimCtrl_Frm_Mov   inventDimFormSetup;
    QuotationType           callerQuotationType;
    SalesQuotationTable     salesQuotationTableRef;
    DocumentStatus          salesQuotationDocumentStatus;

}

void enableFields()
{
    if (CustQuotationConfirmTrans.ProjTransType == QuotationProjTransType::Item)
    {
        custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ItemId)).enabled(true);
        custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjectResource)).enabled(false);
    }
    else
    {
        custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ItemId)).enabled(false);
        custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjectResource)).enabled(true);
    }
}

void init()
{
    boolean     markupAllowed = true;

    super();

    if (element.args().record())
    {
        switch (element.args().record().TableId)
        {
            case tablenum(SalesQuotationTable):
                salesQuotationTableRef       = element.args().record();
                salesQuotationDocumentStatus = element.args().parmEnum();
                callerQuotationType          = salesQuotationTableRef.QuotationType;
                markupAllowed                = salesQuotationTableRef.markupAllowed();

                // if sales quotation id, then show header title with the id.
                if (salesQuotationTableRef.QuotationId)
                {
                    header.text(strFmt("@SYS76498", "@SYS84505", salesQuotationTableRef.QuotationId));
                }

                break;
        }
        element.showHideFields();
    }

    markupTrans_HeadingSales.visible(markupAllowed);
    markupTrans_LineSales.visible(markupAllowed);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxJournalTrans.visible(false);
    }
    // </GTE>
}

void launchReport(PrintCopyOriginal _printCopyOriginal)
{
    RecordSortedList            salesQuotationConfirmList = new RecordSortedList(tableNum(CustQuotationConfirmJour));
    RecordSortedList            projQuotationConfirmList  = new RecordSortedList(tableNum(CustQuotationConfirmJour));
    CustQuotationConfirmJour    cursor;
    str                         salesReportMenuItemName;
    str                         projReportMenuItemName;
    MenuFunction                reportMenuFunction;
    Args                        reportArgs;

    switch (_printCopyOriginal)
    {
        case PrintCopyOriginal::OriginalPrint:
            salesReportMenuItemName = menuitemOutputStr(SalesQuotationConfirmation);
            projReportMenuItemName  = menuitemOutputStr(PSAQuotationsConfirmPrint);
            break;

        case PrintCopyOriginal::Original:
            salesReportMenuItemName = menuitemOutputStr(SalesQuotationOriginalConfirmation);
            projReportMenuItemName  = menuitemOutputStr(PSAQuotationsOriginalConfirm);
            break;

        case PrintCopyOriginal::Copy:
            salesReportMenuItemName = menuitemOutputStr(SalesQuotationCopyConfirmation);
            projReportMenuItemName  = menuitemOutputStr(PSAQuotationsCopyConfirm);
            break;
    }

    for (cursor = getFirstSelection(custQuotationConfirmJour_ds);
    cursor;
    cursor = custQuotationConfirmJour_ds.getNext())
    {
        if (cursor.QuotationType == QuotationType::Project)
        {
            projQuotationConfirmList.ins(cursor);
        }
        else
        {
            salesQuotationConfirmList.ins(cursor);
        }
    }

    if (projQuotationConfirmList.len() > 0)
    {
        reportArgs = new Args();
        reportArgs.object(projQuotationConfirmList);

        reportMenuFunction = new MenuFunction(projReportMenuItemName, MenuItemType::Output);
        reportMenuFunction.run(reportArgs);
    }

    if (salesQuotationConfirmList.len() > 0)
    {
        reportArgs = new Args();
        reportArgs.object(salesQuotationConfirmList);

        reportMenuFunction = new MenuFunction(salesReportMenuItemName, MenuItemType::Output);
        reportMenuFunction.run(reportArgs);
    }
}

void showHideFields()
{
    switch (callerQuotationType)
    {
        case QuotationType::Sales     :
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, SalesId)).visible(true);
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, ProjIdRef)).visible(false);
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, ProjInvoiceProjId)).visible(false);

            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ItemId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, LineAmountTax)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ActivityId)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjTransType)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjectResource)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, LinePropertyId)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjCategoryId)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjDescription)).visible(false);
            break;

        case QuotationType::Project   :
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ItemId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjectResource)).visible(true);
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, SalesId)).visible(false);
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, ProjIdRef)).visible(true);
            custQuotationConfirmJour_ds.object(fieldnum(CustQuotationConfirmJour, ProjInvoiceProjId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, LineAmountTax)).visible(false);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ActivityId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjTransType)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, LinePropertyId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjCategoryId)).visible(true);
            custQuotationConfirmTrans_ds.object(fieldnum(CustQuotationConfirmTrans, ProjDescription)).visible(true);
            salesQuotationShowConfirmation.visible(true);
            break;
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default :
            throw error(strfmt("@SYS54195", funcname()));
    }
}

public void init()
{
    QueryBuildDataSource  queryDataSourceLink;

    super();

    if (element.args().dataset() == tablenum(SalesTable))
    {
        this.query().dataSourceTable(tablenum(CustQuotationConfirmJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tablenum(CustQuotationConfirmJour)).addDataSource(tablenum(CustQuotationConfirmSalesLink));
        queryDataSourceLink.relations(true);

        queryDataSourceLink.addDynalink(fieldnum(CustQuotationConfirmSalesLink, SalesId),
                                        element.args().record(),
                                        fieldnum(SalesTable, SalesId));
    }

    if (element.args().dataset() == tablenum(SalesQuotationTable))
    {
        this.query().dataSourceTable(tablenum(CustQuotationConfirmJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tablenum(CustQuotationConfirmJour)).addDataSource(tablenum(CustQuotationConfirmSalesLink));
        queryDataSourceLink.relations(true);

        queryDataSourceLink.addDynalink(fieldnum(CustQuotationConfirmSalesLink, OrigQuotationId),
                                        element.args().record(),
                                        fieldnum(SalesQuotationTable, QuotationId));
    }
}

public int active()
{
    int ret = super();

    element.enableFields();

    return ret;
}

public void jumpRef()
{
    Args args = new Args();

    custQuotationConfirmJour = CustQuotationConfirmJour::findFromSalesQuotationQuotation(this.text());
    SalesQuotationTable salesQuotationTable = custQuotationConfirmJour.salesQuotationTable();

    args.record(salesQuotationTable);
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(SalesQuotationTableForEdit),MenuItemType::Display);
    menuFunction.run(args);
}
