public void updateDesign()
{
    boolean enableCustomFieldButton = CustInvoiceTemplate::existBillingCodeWithCustomField(custRecurrenceInvoice.CustInvoiceTemplate, dateNull());

    btnCustomField.enabled(enableCustomFieldButton);
}

/// <summary>
/// Deletes the the <c>CustRecurrenceInvoice</c> record from the data source.
/// </summary>
public void delete()
{
    super();
    if (custRecurrenceInvoice.EndDate != dateNull())
    {
        EndDateOptionGroup.selection(RecurrenceEndType::BillingEndDate);
    }
    else
    {
        EndDateOptionGroup.selection(RecurrenceEndType::NoEndDate);
    }
}

/// <summary>
/// Called when a recurrence invoice setup record is selected on the form.
/// </summary>
/// <returns>
/// returns an integer value to the base.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    if (custRecurrenceInvoice.RecId != 0)
    {
        this.object(fieldNum(CustRecurrenceInvoice, CustBillingClassification)).allowEdit(false);
    }
    else
    {
        this.object(fieldNum(CustRecurrenceInvoice, CustBillingClassification)).allowEdit(true);
    }

    if ((custRecurrenceInvoice.EndDate != dateNull()))
    {
        EndDateOptionGroup.selection(RecurrenceEndType::BillingEndDate);
    }
    else
    {
        EndDateOptionGroup.selection(RecurrenceEndType::NoEndDate);
    }
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        custCustomFieldElementsMap = null;
        element.updateDesign();
    }

    intervalText.text(CustRecurrenceInvoice::getIntervalLabel(custRecurrenceInvoice.RecurrencePattern));

    return ret;
}

/// <summary>
/// Initializes the <c>CustRecurrenceInvoice</c> data source.
/// </summary>
public void init()
{
    super();

    this.object(fieldNum(CustRecurrenceInvoice, CustBillingClassification)).visible(CustParameters::isBillingClassificationEnabled());

    if (custRecurrenceInvoice.EndDate != dateNull())
    {
        EndDateOptionGroup.selection(RecurrenceEndType::BillingEndDate);
    }
    else
    {
        EndDateOptionGroup.selection(RecurrenceEndType::NoEndDate);
    }

    custRecurrenceInvoice_ds.cacheAddMethod(tableMethodStr(CustRecurrenceInvoice, displayNextGenerationDate));
    custRecurrenceInvoice_ds.cacheAddMethod(tableMethodStr(CustRecurrenceInvoice, displayLastGenerationDate));
}

