public void write()
{
    CustInvLineBillCodeCustomFieldBase baseValue;
    boolean isCustInvoiceTemplateModified;

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        isCustInvoiceTemplateModified = custRecurrenceInvoice.orig().CustInvoiceTemplate != custRecurrenceInvoice.CustInvoiceTemplate ? true : false;

        ttsbegin;
        if (custRecurrenceInvoice.RecId && isBillingClassificationEnabled)
        {
            if (isCustInvoiceTemplateModified)
            {
                delete_from baseValue where baseValue.CustRecurrenceInvoice == custRecurrenceInvoice.RecId;
            }
            CustInvLineBillCodeCustomFieldBase::writeCustCustomFields(custCustomFieldElementsMap, CustCustomFieldSourceType::CustRecurrenceInvoice, custRecurrenceInvoice);
        }
        ttscommit;
    }
}

/// <summary>
/// Returns the status of the recurrence invoice. The invoice is active if it is not stopped, and not active otherwise.
/// </summary>
public display str isActive(CustRecurrenceInvoice _custRecurrenceInvoice)
{
    return _custRecurrenceInvoice.IsStopped ? "@SYS2604" : "@SYS39462";
}

