/// <summary>
/// Modify the <c>CustRecurrenceInvoice</c> data source.
/// </summary>
public void modified()
{
    CustInvoiceTemplate custInvoiceTemplateLocal;

    custInvoiceTemplateLocal = CustInvoiceTemplate::find(custRecurrenceInvoice.CustInvoiceTemplate);
    super();
    custRecurrenceInvoice.CurrencyCode = custInvoiceTemplateLocal.CurrencyCode;

    custRecurrenceInvoice_ds.rereadReferenceDataSources();

    if (!isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
    element.openCustomField();
    }

    element.updateDesign();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    CustInvoiceTemplate custInvoiceTemplateLocal;
    QueryBuildDataSource custInvoiceTemplateQBDS;

    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(CustInvoiceTemplate),
                                                                                    _formReferenceControl, true);
    Query lookupQuery;

    sysTableLookup.addLookupfield(fieldNum(CustInvoiceTemplate, TemplateName));
    sysTableLookup.addLookupfield(fieldNum(CustInvoiceTemplate, TemplateDescription));

    lookupQuery = new Query();
    custInvoiceTemplateQBDS = lookupQuery.addDataSource(tableNum(CustInvoiceTemplate));

    if (isBillingClassificationEnabled)
    {
        custInvoiceTemplateQBDS.addRange(fieldNum(CustInvoiceTemplate,
                                                BillingClassification)).value(SysQuery::value(custRecurrenceInvoice.CustBillingClassification));
    }

    sysTableLookup.parmQuery(lookupQuery);

    custInvoiceTemplateLocal = sysTableLookup.performFormLookup();

    return custInvoiceTemplateLocal;
}

public boolean validate()
{
    boolean ret;

    if (custRecurrenceInvoice.EndDate && (custRecurrenceInvoice.EndDate > custRecurrenceInvoice.StartDate))
    {
        ret = super();
    }
    else
    {
        if (custRecurrenceInvoice.EndDate != dateNull())
        {
            warning("@SYS315502");
            ret = false;
        }
        else
        {
            ret = super();
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    if (custRecurrenceInvoice.EndDate && (custRecurrenceInvoice.EndDate <= custRecurrenceInvoice.StartDate))
    {
        warning("@SYS315502");
        ret = false;
    }
    else
    {
        ret = super();
    }

    return ret;
}

