public common lookupReference()
{
    Query lookupQuery = new Query();
    QueryBuildDataSource custInvoiceTemplateQBDS;
    SysReferenceTableLookup sysTableLookup;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && custRecurrenceInvoice.CustBillingClassification)
    {
        sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(CustInvoiceTemplate), this, true);

        sysTableLookup.addLookupfield(fieldNum(CustInvoiceTemplate, TemplateName));
        sysTableLookup.addLookupfield(fieldNum(CustInvoiceTemplate, TemplateDescription));

        custInvoiceTemplateQBDS = lookupQuery.addDataSource(tableNum(CustInvoiceTemplate));

        if (isBillingClassificationEnabled)
        {
            custInvoiceTemplateQBDS.addRange(
                fieldNum(CustInvoiceTemplate,BillingClassification)).value(SysQuery::value(custRecurrenceInvoice.CustBillingClassification));
        }

        sysTableLookup.parmQuery(lookupQuery);

        return sysTableLookup.performFormLookup();
    }
    else
    {
        return super();
    }
}

