public int selectionChange()
{
    int ret;

    ret = super();
    if (this.Selection() == RecurrenceEndType::BillingEndDate)
    {
        custRecurrenceInvoice_ds.object(fieldNum(CustRecurrenceInvoice, EndDate)).allowEdit(true);
        custRecurrenceInvoiceSetup_EndDate.allowEdit(true);
    }
    else if (this.Selection() == RecurrenceEndType::NoEndDate)
    {
        custRecurrenceInvoice.EndDate = dateNull();
        custRecurrenceInvoiceSetup_EndDate.dateValue(dateNull());
        CustRecurrenceInvoiceSetup_EndDate.allowEdit(false);
        custRecurrenceInvoice_ds.refresh();
    }
    return ret;
}

/// <summary>
/// Sets the recurrence invoice interval text value based on the valu selected on <c>RecurrencePattern</c>.
/// </summary>
/// <returns>
/// returns an boolean value to the base.
/// </returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        intervalText.text(CustRecurrenceInvoice::getIntervalLabel(custRecurrenceInvoice_RecurrencePattern.selection()));
    }

    return ret;
}
