/// <summary>
/// Enable form actions.
/// </summary>
public void enableActions()
{
    boolean canPost;
    boolean canValidate;
    boolean canDelete;
    // <GEERU>
    boolean canUpdateFacture;
    boolean canUpdateInvoice4Paym;
    // </GEERU>

    if (custRecurrenceInvoiceGroup)
    {
        if (!custRecurrenceInvoiceGroup.Posted)
        {
            // Customer recurrence invoices can be validated because they have not been posted yet.
            canValidate = true;

            if (CustRecurrenceInvoiceGroup::isWorkflowActive(custRecurrenceInvoiceGroup.RecId))
            {
                switch (custRecurrenceInvoiceGroup.WorkflowApprovalState)
                {
                    case CustFreeInvoiceWFApprovalState::NotSubmitted:
                        // Customer recurrence invoices can be deleted because the document has not been submitted to workflow yet.
                        canDelete = true;

                        if (!custRecurrenceInvoiceGroup.canSubmitToWorkflow())
                        {
                            // Customer recurrence invoices can be posted because the document cannot be submitted to workflow even though the workflow is activated.
                            canPost = true;
                        }

                        break;

                    case CustFreeInvoiceWFApprovalState::WorkflowCompleted:
                        // Customer recurrence invoices can be posted or deleted because their associated active workflow is completed.
                        canPost = true;
                        canDelete = true;
                        break;

                    default:
                        break;
                }
            }
            else
            {
                // Customer recurrence invoices can be posted or deleted because they are not associated with an active workflow.
                canPost = true;
                canDelete = true;
            }

            // <GEERU>
            canUpdateFacture = canPost;
            canUpdateInvoice4Paym = true;
            // </GEERU>
        }
        // <GEERU>
        else
        {
            canUpdateFacture = custRecurrenceInvoiceGroup.canUpdateFacture();
        }
        // </GEERU>
    }
    else
    {
        canDelete = true;
    }

    // <GEERU>
    CustReccurenceFacture.enabled (canUpdateFacture);
    CustReccurenceInvoice4Paym.enabled(canUpdateInvoice4Paym);
    // </GEERU>

    if (canValidate)
    {
        custRecurrenceInvoiceCheck.enabled(true);

        if (canPost)
        {
            custRecurrenceInvoicePost.enabled(true);
            custRecurrenceInvoicePostTransfer.enabled(true);
        }
        else
        {
            custRecurrenceInvoicePost.enabled(false);
            custRecurrenceInvoicePostTransfer.enabled(false);
        }
    }
    else
    {
        // Not valid to be able to post but not allowed to validate.
        Debug::assert(!canPost);

        custRecurrenceInvoiceCheck.enabled(false);
        custRecurrenceInvoicePost.enabled(false);
        custRecurrenceInvoicePostTransfer.enabled(false);
    }

    if (canDelete)
    {
        custRecurrenceInvoiceGroup_ds.allowDelete(true);
    }
    else
    {
        CustRecurrenceInvoiceGroup_DS.allowDelete(false);
    }
}

/// <summary>
/// Called when a recurrence invoice group is selected on the form.
/// </summary>
/// <returns>
/// Returns an integer value to the base.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.enableActions();
    }

    return ret;
}
