public int active()
{
    int ret;

    ret = super();

    ledgerTransVoucherButton_RU.enabled(custSettlement.TaxVoucher_RU != "");

    return ret;
}

public void init()
{
    super();
    this.cacheAddMethod(tableMethodStr(CustSettlement, displayReverseDate_RU));
    this.cacheAddMethod(tableMethodStr(CustSettlement, displayInvoiceAmountMST));
    this.cacheAddMethod(tableMethodStr(CustSettlement, displayInvoiceCurrencyCode));
    this.cacheAddMethod(tableMethodStr(CustSettlement, displayInvoiceId));
}

public void init()
{
    super();

    //Adding the join to the SubledgerVoucherGeneralJournalEntry table
    this.query(SubledgerVoucherGeneralJournalEntry::addJoinForNaturalKey(this.query(), tablenum(CustTrans),
                                                                            fieldnum(CustTrans, TransDate),
                                                                            fieldnum(CustTrans, Voucher),
                                                                            fieldnum(CustTrans, DataAreaId)));
}

/// <summary>
/// The query is executed when a form is opened for data display.
/// </summary>
public void executeQuery()
{
    this.query().dataSourceNo(1).addSortField(fieldnum(CustSettlementLine, CustInvoiceLineIdRef));

    super();
}

/// <summary>
/// Gets the invoice line description.
/// </summary>
/// <param name="_custSettlementLine">
/// The <c>CustSettlementLine</c> table buffer.
/// </param>
/// <returns>
/// Returns the description for invoice line.
/// </returns>
public display Description displayLineDescription(CustSettlementLine _custSettlementLine)
{
    CustInvoiceLineIdRef    custInvoiceLineIdRef;
    CustInvoiceTrans        custInvoiceTrans;
    CustInterestTrans       custInterestTrans;
    MarkupTrans             markupTrans;
    Description             lineDescription;

    custInvoiceLineIdRef = CustInvoiceLineIdRef::find(_custSettlementLine.CustInvoiceLineIdRef);

    if (CustInvoiceLineType::Customer == custInvoiceLineIdRef.EntityType)
    {
        custInvoiceTrans = CustInvoiceTrans::findByCustInvoiceLineRefId(custInvoiceLineIdRef.RecId);

        if (custInvoiceTrans)
        {
            lineDescription = custInvoiceTrans.Name;
        }
    }
    else if (CustInvoiceLineType::InterestNote == custInvoiceLineIdRef.EntityType)
    {
        custInterestTrans = CustInterestTrans::findByCustInvoiceLineRefId(custInvoiceLineIdRef.RecId);

        if (custInterestTrans)
        {
            lineDescription = custInterestTrans.Txt;
        }
    }
    else if (CustInvoiceLineType::MarkupTrans == custInvoiceLineIdRef.EntityType)
    {
        markupTrans = MarkupTrans::findByCustInvoiceLineRefId(custInvoiceLineIdRef.RecId);

        if (markupTrans)
        {
            lineDescription = markupTrans.Txt;
        }
    }

    return lineDescription;
}
