public void init()
{
    super();

    element.initSettlementPriority();
    CustSettlementPriority::createData();
    // ensure transaction type mapping data has been created
    CustLedgerTransTypeMapping::createData();

    BillingClassification.enabled(CustParameters::isBillingClassificationEnabled());
}

/// <summary>
/// Initializes the <c>ListTransactionTypes</c> listbox control by fetching record from <c>CustSettlementPriority</c> table.
/// </summary>
public void initSettlementPriority()
{
    List                    priorityList;
    ListEnumerator          priorityListEnumerator;
    CustSettlementTrans     transType;

    listTransactionTypes.clear();

    // Get ordered list from CustSettlementPriority Table
    priorityList = CustSettlementTransactionPriority::getOrderedTransactionTypes();

    // initialize the listbox control
    if (!priorityList.empty())
    {
        priorityListEnumerator = priorityList.getEnumerator();
        while (priorityListEnumerator.moveNext())
        {
            transType = priorityListEnumerator.current();
            listTransactionTypes.add(enum2str(transType));
        }
    }
    listTransactionTypes.update();
}

public void updateDesign()
{
    boolean isEnable = custSettlementPriority.Enabled && !this.inViewMode();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        billingDetails.enabled(isEnable);
    }
    settlementPriority.enabled(isEnable);
}

public int task(int _taskId)
{
    #task

    int ret = super(_taskId);

    switch (_taskId)
    {
        case #taskEditRecord:
        case #taskDoneEditRecord:
            CustSettlementPriority_ds.active();
            break;
    }

    return ret;
}

/// <summary>
/// Customer settlement priority data source on activated.
/// </summary>
/// <remarks>
/// This method is an extensibility point that is rasied after the <c>CustSettlementPriority</c> data source active method is invoked.
/// </remarks>
protected void custSettlementPriorityDataSourceOnActivated()
{
    return;
}

public void init()
{
    super();

    this.query().dataSourceNo(1).orderMode(OrderMode::OrderBy);
    this.query().dataSourceNo(1).addSortField(fieldNum(CustSettlementPriority, Priority), SortOrder::Descending);
    if (!CustParameters::isBillingClassificationEnabled())
    {
        this.query().dataSourceName(this.name()).addRange(fieldNum(CustSettlementPriority, Attribute)).value(SysQuery::valueNot('Billing'));
    }
}

public int active()
{
    int ret;
    int maxPriorityIdx = 1;
    int minPriorityIdx = custSettlementPriority_ds.numberOfRowsLoaded();
    int secondLastIdx = minPriorityIdx - 1;

    ret = super();
    element.updateDesign();

    if (element.inViewMode())
    {
        moveUp.enabled(false);
        moveDown.enabled(false);
    }
    else
    {
        switch (custSettlementPriority_ds.getPosition())
        {
            // When attribute with least priority is selected i.e. Voucher.
            // Both the Up and Down buttons should be disabled. So that user cannot change its Priority.
            case minPriorityIdx:
                moveUp.enabled(false);
                moveDown.enabled(false);
                break;

            // When attribute with highest priority is selected.
            // Only the Down button should be enabled.
            case maxPriorityIdx:
                moveUp.enabled(false);
                moveDown.enabled(true);
                break;

            // When the attribute with second last priority is selected.
            // Only the Up button should be enabled as Voucher will always have the least priority.
            case secondLastIdx:
                moveUp.enabled(true);
                moveDown.enabled(false);
                break;

            default:
                moveUp.enabled(true);
                moveDown.enabled(true);
                break;
        }
    }

    switch (custSettlementPriority.Attribute)
    {
        case CustSettlementPriorityAttribute::DueDate, CustSettlementPriorityAttribute::TranscationAmount, CustSettlementPriorityAttribute::TransactionDate:
            settlementPriority.visible(false);
            custSettlementPriority_Direction.visible(true);
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                billingDetails.visible(false);
            }
            custSettlementPriority_Enabled1.enabled(true);
            break;

        case CustSettlementPriorityAttribute::TransactionType:
            settlementPriority.visible(true);
            custSettlementPriority_Direction.visible(false);
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                billingDetails.visible(false);
            }
            custSettlementPriority_Enabled1.enabled(true);
            break;

        case CustSettlementPriorityAttribute::Billing :
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                settlementPriority.visible(false);
                custSettlementPriority_Enabled1.enabled(true);

                if (custparameters.MarkOpenInvoiceLine == NoYes::Yes)
                {
                    custSettlementPriority_Direction.visible(false);
                    billingDetails.visible(true);
                }
                else
                {
                    billingDetails.visible(false);
                    checkFailed(strFmt("@SPS528","@SPS1138","@SPS1139"));
                    custSettlementPriority_Direction.visible(false);
                }
            }
            break;

        case CustSettlementPriorityAttribute::CashDiscountDate :
            settlementPriority.visible(false);
            custSettlementPriority_Direction.visible(false);
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                billingDetails.visible(false);
            }
            custSettlementPriority_Enabled1.enabled(true);
            break;

        case CustSettlementPriorityAttribute::Voucher:
            settlementPriority.visible(false);
            custSettlementPriority_Direction.visible(false);
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                billingDetails.visible(false);
            }
            custSettlementPriority_Enabled1.enabled(false);
            break;
    }

    if (custSettlementPriority.Direction == CustSettlementAttributeDirection::Ascending)
    {
        custSettlementPriority_Direction.selection(0);
    }
    else
    {
        custSettlementPriority_Direction.selection(1);
    }

    element.custSettlementPriorityDataSourceOnActivated();

    return ret;
}

public int active()
{
    int ret;
    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (custparameters.InvoiceLinePriority == CustInvoiceLinePriority::None)
        {
            spanbillcode.enabled(false);
            prorationType.enabled(false);
        }
        else
        if (custparameters.InvoiceLinePriority == CustInvoiceLinePriority::BillingCode)
        {
            spanbillcode.enabled(true);
            prorationType.enabled(false);
        }
        else if (custparameters.InvoiceLinePriority == CustInvoiceLinePriority::Proration)
        {
            spanbillcode.enabled(false);
            prorationType.enabled(true);
        }
    }

    return ret;
}

void clicked()
{
    int curPos = custSettlementPriority_ds.getPosition();
    CustSettlementPriority custSettleCurrent;
    CustSettlementPriority custSettleNew;

    super();

    Debug::assert(curPos > 1);

    custSettleCurrent = custSettlementPriority_ds.cursor(curPos);
    custSettleNew = custSettlementPriority_ds.cursor(curPos - 1);

    CustSettlementPriority::swapPriority(custSettleCurrent.Attribute, custSettleNew.Attribute);
    custSettlementPriority_ds.executeQuery();
    custSettlementPriority_ds.setPosition(curPos - 1);
}

void clicked()
{
    int curPos = custSettlementPriority_ds.getPosition();
    int maxPos = custSettlementPriority_ds.numberOfRowsLoaded();
    CustSettlementPriority custSettleCurrent;
    CustSettlementPriority custSettleNew;

    super();

    Debug::assert(curPos < maxPos);

    custSettleCurrent = custSettlementPriority_ds.cursor(curPos);
    custSettleNew = custSettlementPriority_ds.cursor(curPos + 1);

    CustSettlementPriority::swapPriority(custSettleCurrent.Attribute, custSettleNew.Attribute);
    custSettlementPriority_ds.executeQuery();
    custSettlementPriority_ds.setPosition(curPos + 1);
}

