public int selectionChange()
{
    int ret;
    int minSettleTransIdx = this.count() - 1;
    int maxSettleTransIdx = 0;
    ret = super();

    switch (this.selection())
    {
        case minSettleTransIdx:
            buttonDown.enabled(false);
            buttonUp.enabled(true);
            break;

        case maxSettleTransIdx:
            buttonUp.enabled(false);
            buttonDown.enabled(true);
            break;

        default:
            buttonUp.enabled(true);
            buttonDown.enabled(true);
            break;
    }

    return ret;
}

/// <summary>
/// Increases the priority of the selected transaction type.
/// </summary>
/// <remarks>
/// It updates record in <c>CustSettlementPriority</c> table and then refreshes the listbox control holding transaction types.
/// </remarks>

void clicked()
{
    CustSettlementTrans     custSettlementTrans;
    int                     selectionIndex;

    super();

    selectionIndex = listTransactionTypes.selection();
    custSettlementTrans = str2enum(custSettlementTrans, listTransactionTypes.getText(selectionIndex));
    CustSettlementTransactionPriority::updatePriorityOrder(custSettlementTrans, true);

    // refreshes the listbox
    element.initSettlementPriority();

    // set the selection position to the previous trans type
    listTransactionTypes.selection(selectionIndex - 1);
    listTransactionTypes.selectionChange();
}

/// <summary>
/// Decreases the priority of the selected transaction type.
/// </summary>
/// <remarks>
/// It updates record in <c>CustSettlementPriority</c> table and then refreshes the listbox control holding transaction types.
/// </remarks>

void clicked()
{
    CustSettlementTrans     custSettlementTrans;
    int                     selectionIndex;
    super();

    selectionIndex = listTransactionTypes.selection();

    custSettlementTrans = str2enum(custSettlementTrans, listTransactionTypes.getText(selectionIndex));
    CustSettlementTransactionPriority::updatePriorityOrder(custSettlementTrans, false);

    // refreshes the listbox
    element.initSettlementPriority();

    // set the selection position to the previous trans type
    listTransactionTypes.selection(selectionIndex + 1);
    listTransactionTypes.selectionChange();
}

public boolean modified()
{
    boolean ret;
    ret = super();

    if (custparameters.InvoiceLinePriority == CustInvoiceLinePriority::None)
    {
        spanbillcode.enabled(false);
        prorationType.enabled(false);
    }
    else if (custparameters.InvoiceLinePriority == CustInvoiceLinePriority::BillingCode)
    {
        spanbillcode.enabled(true);
        custParameters.SpanBillingCodeAcrossInvoices=NoYes::No;
        prorationType.enabled(false);
    }
    else if (custparameters.InvoiceLinePriority ==  CustInvoiceLinePriority::Proration)
    {
        spanbillcode.enabled(false);
        prorationType.enabled(true);
        custparameters.ProrationType=CustSettlementProrationType::Equal;
    }

    return ret;
}
