public class CustSpecTransOverview extends FormRun
{
    CustSpecTransOverviewFormMode lastSelection = CustSpecTransOverviewFormMode::Journal;
    Map mapSelection2TabControl;

}

/// <summary>
/// Delegate for when the ViewComboBox is modified.
/// </summary>
/// <param name = "_element">
/// The <c>CustSpecTransOverview</c> form instance.
/// </param>
/// <remarks>
/// <param name = "_eventHandlerResult">
/// An <c>EventHandlerResult</c> where the the data for the data for TempCustVendTrans can be returned.
/// </param>
/// <remarks>
/// This delegate allows <c>CustSpectTransOverviewFormMode</c> extensions to get the data for TempCustVendTrans.
/// </remarks>
delegate void viewComboBoxModifiedGetTmpCustVendTransDelegate(FormRun _element, EventHandlerResult _eventHandlerResult)
{
}

/// <summary>
/// Delegate to add the relationship between the extended <c>CustSpecTransOverviewFormMode</c> and a tab control.
/// </summary>
/// <param name = "_custSpecTransOverviewFormMode2TabControl">
/// A Map that holds the relationship betwween <c>CustSpecTransOverviewFormMode</c> and a tab control.
/// </param>
delegate void initAddCustSpecTransOverviewFormModeAndTabRelationship(Map _custSpecTransOverviewFormMode2TabControl)
{
}

public void init()
{
    super();

    mapSelection2TabControl = new Map(Types::Enum, Types::Class);
    mapSelection2TabControl.insert(CustSpecTransOverviewFormMode::Journal, tabJournals);
    mapSelection2TabControl.insert(CustSpecTransOverviewFormMode::SalesOrder, tabSales);
    mapSelection2TabControl.insert(CustSpecTransOverviewFormMode::Invoice, tabInvoiceJour);
    mapSelection2TabControl.insert(CustSpecTransOverviewFormMode::PaymentProposal, tabPaymProposal);
    mapSelection2TabControl.insert(CustSpecTransOverviewFormMode::Customer, tabSpecContext);

    this.initAddCustSpecTransOverviewFormModeAndTabRelationship(mapSelection2TabControl);

    // Disable the company accounts field on the general tab if Intercompany transactions are disabled
    if (!isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
    {
        ledgerJournalTrans_Company.allowEdit(false);
    }

    LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_AccountNum1.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_OffsetAccount1.parmPostingType(LedgerPostingType::LedgerJournal);
    GroupProposalOverview_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    PaymAccount_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);

    element.design().caption("@SYS53650");
}

public void run()
{
    super();

    CustSpecTransOverviewFormMode documentType = this.findDocumentWithSpecTrans();
    this.changeDocumentSelection(documentType);
}

private CustSpecTransOverviewFormMode findDocumentWithSpecTrans()
{
    CustSpecTransOverviewFormMode documentType;

    if (SpecTransSales)
    {
        documentType = CustSpecTransOverviewFormMode::SalesOrder;
    }
    else if (SpecTransInvoiceJour)
    {
        documentType = CustSpecTransOverviewFormMode::Invoice;
    }
    else if (SpecTransPaymProposal)
    {
        documentType = CustSpecTransOverviewFormMode::PaymentProposal;
    }
    else if (SpecTransSpecContext)
    {
        documentType = CustSpecTransOverviewFormMode::Customer;
    }
    else
    {
        documentType = this.defaultCustSpecTransOverviewFormMode();
    }

    return documentType;
}

public CustSpecTransOverviewFormMode defaultCustSpecTransOverviewFormMode()
{
    return CustSpecTransOverviewFormMode::Journal;
}

private void changeDocumentSelection(CustSpecTransOverviewFormMode _newSelection)
{
    if (_newSelection != lastSelection)
    {
        ViewComboBox.selection(_newSelection);

        this.selectActiveTab(_newSelection);
    }
}

private void selectActiveTab(CustSpecTransOverviewFormMode _newSelection)
{
    switch (_newSelection)
    {
        case CustSpecTransOverviewFormMode::Journal:
            tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Cust, ledgerJournalTrans.company(), ledgerJournalTrans.TableId, ledgerJournalTrans.RecId));
            break;

        case CustSpecTransOverviewFormMode::SalesOrder:
            tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Cust, salesTable.company(), salesTable.TableId, salesTable.RecId));
            break;

        case CustSpecTransOverviewFormMode::Invoice:
            tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Cust, custInvoiceTable.company(), custInvoiceTable.TableId, custInvoiceTable.RecId));
            break;

        case CustSpecTransOverviewFormMode::PaymentProposal:
            tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Cust, custInvoiceTable.company(), custInvoiceTable.TableId, custInvoiceTable.RecId));
            break;

        case CustSpecTransOverviewFormMode::Customer:
            tmpCustVendTrans.setTmpData(CustVendFindSettlements::findMarkedTrans(ModuleCustVend::Cust, specContext.company(), specContext.TableId, specContext.RecId));
            break;

        default:
            EventHandlerResult result = EventHandlerResult::newSingleResponse();
            element.viewComboBoxModifiedGetTmpCustVendTransDelegate(element, result);
            if (result.hasResult())
            {
                tmpCustVendTrans.setTmpData(result.result());
            }
            break;
    }

    FormTabControl tabControl = mapSelection2TabControl.lookup(lastSelection);
    tabControl.visible(false);

    tabControl = mapSelection2TabControl.lookup(_newSelection);
    tabControl.visible(true);

    lastSelection = _newSelection;
}

public int active()
{
    int ret;

    ret = super();

    tmpCustVendTrans.setTmpData(
        CustVendFindSettlements::findMarkedTrans(
            ModuleCustVend::Cust,
            ledgerJournalTrans.company(),
            ledgerJournalTrans.TableId,
            ledgerJournalTrans.RecId));
    tmpCustVendTrans_DS.research();

    LedgerJournalTrans_AccountNum.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_AccountNum1.parmDataAreaId(ledgerJournalTrans.Company);
    LedgerJournalTrans_OffsetAccount1.parmDataAreaId(ledgerJournalTrans.getOffsetCompany());

    return ret;
}

