/// <summary>
///    This method allows or restricts editing of the <c>Issue own certificate</c> field
///    depending on the value of the <c>Entry certificate required</c> field.
/// </summary>
void allowEditIssueOwnEntryCertificate_W()
{
    custTable_ds.object(fieldNum(CustTable, IssueOwnEntryCertificate_W)).allowEdit(custTable.EntryCertificateRequired_W);
}

void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Pass back customer account to (potential) caller, but only if customer
        // record exists.
        if (custTable.RecId)
        {
            custTableConfigure = new MCRCustTableConfigure();
            custTableConfigure.parmCustAccount(custTable.AccountNum);
            element.args().parmObject(custTableConfigure);
        }
    }
    super();
}

/// <summary>
///     Creates a new record for the current data source.
/// </summary>
/// <param name = "_formDataSourceName">
///     Name of the current form datasource.
/// </param>
/// <param name = "_append">
///     flag to append the datasource, Optional.
/// </param>
public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (this.shouldCreateRecordBasedOnDirParty(_formDataSourceName))
    {
        partyForm.initValue();
        partyForm.create();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

/// <summary>
///     Determines whether the new record should be created based on DirParty.
/// </summary>
/// <param name = "_formDataSourceName">
///     Name of the current form datasource.
/// </param>
/// <returns>
///     true if the new record should be created based on DirParty; otherwise, false.
/// </returns>
protected boolean shouldCreateRecordBasedOnDirParty(str _formDataSourceName)
{
    return conFind(mainDatasources, this.dataSource(_formDataSourceName).id());
}

/// <summary>
///    Disables and enables field controls for when this form is called from <c>InventTrackingDimTracing</c> form.
/// </summary>
/// <remarks>
///    This is specific to tracing and will not be used in the normal calling path.
/// </remarks>
private void disableEnableControlsForItemTracing()
{
    custTable_Currency.visible(false);
    custTable_CustClassificationId.visible(true);
}

public void enableCreditCardVerification()
{
    if (CreditCardProcessors::activeProcessorExists())
    {
        if (CustParameters::find().CreditCardPreAuthorization == NoYes::Yes)
        {
            creditCardProcessing_CreditCardAddressVerification.enabled(true);
            creditCardProcessing_CreditCardCVC.enabled(true);
        }
        else
        {
            creditCardProcessing_CreditCardAddressVerification.enabled(false);
            creditCardProcessing_CreditCardCVC.enabled(false);
        }

        if (creditCardProcessing_CreditCardAddressVerification.enabled() &&
                custTable.CreditCardAddressVerification == CreditCardAddressVerification::Yes)
        {
            creditCardProcessing_CreditCardAddressVerificationVoid.enabled(true);
            creditCardProcessing_CreditCardAddressVerificationLevel.enabled(true);
        }
        else
        {
            creditCardProcessing_CreditCardAddressVerificationVoid.enabled(false);
            creditCardProcessing_CreditCardAddressVerificationLevel.enabled(false);
        }
    }
    else
    {
        creditCardProcessing.visible(false);
    }
}

public void enableRfcCurpFields_MX(CustTable _custTable)
{
    boolean isMexicanEntityOrPerson = (_custTable.CompanyType_MX == CompanyType_MX::LegalEntity) || (_custTable.CompanyType_MX == CompanyType_MX::LegalPerson);

    custTable_ds.object(fieldNum(CustTable, Curp_MX)).enabled(isMexicanEntityOrPerson);
    custTable_ds.object(fieldNum(CustTable, StateInscription_MX)).enabled(isMexicanEntityOrPerson);
}

void hideQualityManagement()
{
    // set NonConformanceTable menu item visibility to false
    nonConformance.visible(false);
}

/// <summary>
///  Disables all fields except reference until the user has filled in a reference
/// </summary>
/// <remarks>
///  Form opens in create new customer, modify existing customer, etc.  Sets the
///  mode based on based on the mode in use when the user last closed the form.
/// </remarks>
void init()
{
    boolean       isCustTableDataSet = false;
    boolean       custTableConfigureEnabled = classIdGet(element.args().parmObject()) == classIdGet(custTableConfigure);
    // <GIN>
    TaxWithholdParameters_IN    taxWithholdParameters = TaxWithholdParameters_IN::find();
    // </GIN>

    // <GCN>
    TaxIntegrationSystemEnabled_CN  taxIntegrationSystemEnabled_CN;
    // </GCN>        

    custParameters = CustParameters::find();

    traceFilter = element.args().parmObject() as InventTrackingCustomerSummaryFilter;
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    if (element.args().dataset() == tableNum(CustTable))
    {
        isCustTableDataSet = true;
    }

    typeComboBox = DirPartyFormHandler::updateTypeComboBox(element, formControlStr(CustTable, Type));

    if (element.args().parmObject()
        && custTableConfigureEnabled)
    {
        custTableConfigure = element.args().parmObject();
    }

    if (element.args())
    {
        callerRecord = element.args().record();
    }

    super();

    element.markChangeProposalFields();

    if (element.args().openMode() != OpenMode::New)
    {
        //
        // Setting the new record action in code rather than having it set on the form design
        // so that the form does an in-line create if it was opened in New mode
        //
        element.design().newRecordAction(formControlStr(CustTable, NewCustomer));
    }

    // <GIN>
    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        taxInformationCustTable_IN_ds.linkType(DataSourceLinkTypePropertyValues::Passive);
    }
    // </GIN>

    TaxVATNumTable::enableLookupVATNum(vatNum);

    #SysSystemDefinedButtons
    OfficeAppCopilotForFinanceFeatureCallout::loadCopilotForFinanceFeatureCallout(#SystemDefinedManageViewFilters);

    if (CompanyNAFCode::nafCodeTableEmpty())
    {
        custTable_ds.object(fieldNum(CustTable, CompanyIdSiret)).visible(false);
        custTable_ds.object(fieldNum(CustTable, CompanyNAFCode)).visible(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
        && element.args().parmObject()
        && custTableConfigureEnabled)
    {
        mcrMIBCustomerService.visible(custTableConfigure.parmShowCustomerServiceButton());
    }

    if (element.args().parm() == formStr(CaseDetail))
    {
        custTable_ds.allowEdit(false);
    }

    var hideShowComboBoxValue = element.configurationHelper().getConfigurationValue(hideShowComboBoxSavedValue);
    HideShowComboBox.selection(str2int(hideShowComboBoxValue));

    mainDatasources = [custTable_ds.id(), dirPartyTable_ds.id(), dirPartyTable_DirPerson_ds.id(), dirPersonName_ds.id(), dirPartyTable_DirOrganization_ds.id(), dirPartyTable_DirOrganizationBase_ds.id()];

    addressBooksCtrl = SysLookupMultiSelectCtrl::construct(element, addressBooks, queryStr(DirAddressBookLookup));

    partyForm = new DirPartyFormHandler_Entity(element);
    partyForm.setDatasourceCaller(custTable_ds);
    partyForm.setDatasourceParty(dirPartyTable_ds);
    partyForm.setDatasourcePartyName(dirPersonName_ds);
    partyForm.ctrlHideShowButton(hideShowButton);
    partyForm.ctrlHideShowComboBox(hideShowComboBox);
    partyForm.ctrlTabPageGeneral(tabGeneral);
    partyForm.parmAddressBookCtrl(addressBooksCtrl);
    partyForm.setNameControls(org_Name, name_FirstName, name_MiddleName, name_LastName);

    hideShowButton.visible(false);
    hideShowComboBox.selection(true);

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN()
        || TaxWithholdParameters_IN::checkTaxParameters())
    {
        element.setVisible_IN();
        if (TaxParameters::isCustomsEnable_IN())
        {
            element.setCustomsGroupVisible_IN();
        }
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, tdsGroup)).visible(taxWithholdParameters.tdsActivate);
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, tcsGroup)).visible(taxWithholdParameters.tcsActivate);
        custTable_ds.object(fieldNum(CustTable, TaxWithholdCalculate_IN)).visible(taxWithholdParameters.tdsActivate || taxWithholdParameters.tcsActivate);
        withholdingTax_IN.visible(taxWithholdParameters.tdsActivate || taxWithholdParameters.tcsActivate);
    }
    // </GIN>

    // <GCN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN]))
    {
        taxIntegrationSystemEnabled_CN = custParameters.TaxIntegrationSystem_CN;
    }
    // </GCN>

    // <GJP>
    if (!CustConsInvoiceType_JP::isCustConsInvoiceEnabled())
    {
        custTable_ds.object(fieldNum(CustTable, ConsDay_JP)).visible(false);
        consDay_JP.visible(false);
    }
    // </GJP>

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    boolean singleLegalEntity = !(CustTableAllowCrossCompanyEmployeeFlight::instance().isEnabled() && CustParameters::find().AllowCrossCompanyEmployee);
    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, singleLegalEntity);
    setPartyType = false;

    if (MCROrderParameters::find().mcrFraudCheck)
    {
        mcrBtnGrpFraudHold.visible(true);
        mcrCustTable_CustStatus.visible(true);
    }
    else
    {
        mcrBtnGrpFraudHold.visible(false);
        mcrCustTable_CustStatus.visible(false);
    }

    if (traceFilter)
    {
        traceFilter.formInitPost();
        this.disableEnableControlsForItemTracing();
    }

    custTable_ds.object(fieldNum(CustTable, EntryCertificateRequired_W)).visible(custParameters.EntryCertificateManagementEnabled_W);
    custTable_ds.object(fieldNum(CustTable, IssueOwnEntryCertificate_W)).visible(custParameters.EntryCertificateIssuingEnabled_W);

    collectionLetterNum.visible(custParameters.CollectionLetterGenerationLevel == CustCollectionLetterGenerationLevel::Customer);
    collectionLetterCode.visible(custParameters.CollectionLetterGenerationLevel == CustCollectionLetterGenerationLevel::Customer);
    collectionLetterDate.visible(custParameters.CollectionLetterGenerationLevel == CustCollectionLetterGenerationLevel::Customer);
    custTable_CollectionLetterCode.visible(custParameters.CollectionLetterGenerationLevel == CustCollectionLetterGenerationLevel::Customer);

    boolean projOperationsIntegrationFeatureEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    if(projOperationsIntegrationFeatureEnabled)
    {
        mibSalesQuotationProjTableForNew.visible(false);
        mibSalesQuotationProjTableForNew.enabled(false);
        btngrpProjectsNew.visible(false);
        btngrpProjectsNew.enabled(false);
        btngrpProjectsProjectControl.visible(false);
        btngrpProjectsProjectControl.enabled(false);
    }

    if(FeatureStateProvider::isFeatureEnabled(TaxIntgrExportDocumentSplitCustValidationFeature_CN::instance()))
    {
        TaxIntgrExportParams_CN.caption("@TaxIntgr:DisableValidation");
        taxIntgrExportParams_CN_SimplifyTaxIntgrExportDocValidation_CN.label("@SYS24585");
        taxIntgrExportParams_CN_SimplifyTaxIntgrExportDocValidation_CN.helpText("@TaxIntgr:DisablingValidationBankAccount");
    }

    if (this.shouldDisplayOverrideSalesTax())
    {
        CustTable_ds.object(fieldNum(CustTable, OverrideSalesTax)).visible(true);
    }

    if (FeatureStateProvider::isFeatureEnabled(CustTableFormCustomSearchFeature::instance()))
    {
        CustTableSearchType.visible(true);
        SearchString.visible(true);
    }

    // Visibility handling of the Prepayment configuration group
    PrepaymentConfig.visible(FeatureStateProvider::isFeatureEnabled(CustPrepaymentInvoiceFeature::instance()));
    Prepayment.visible(FeatureStateProvider::isFeatureEnabled(CustPrepaymentInvoiceFeature::instance()));
}

private boolean shouldDisplayOverrideSalesTax()
{
    return Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Sales)
        || Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::FTI)
        || Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Journal);
}

