NumberSeqFormHandler numberSeqFormHandler()
{
    CustTable customer = CustTable_ds.cursor();
    CustGroup custGroup = CustGroup::find(customer.CustGroup);

    if (custGroup.CustAccountNumSeq)
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(CustGroup.CustAccountNumSeq,
                                                            element,
                                                            custTable_DS,
                                                            fieldNum(CustTable, AccountNum)
                                                        );
        }
        else
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(CustParameters::numRefCustAccount().NumberSequenceId,
                                                            element,
                                                            custTable_DS,
                                                            fieldNum(CustTable, AccountNum)
                                                        );
        }

    return numberSeqFormHandler;
}

public DirPartyFormHandler_Entity parmPartyForm()
{
    return partyForm;
}

void refreshHeaderTabPages()
{
    if (tabDirectDebitMandate.isActivePage() || tabDirectDebitMandate.isExpanded())
    {
        custDirectDebitMandate_ds.executeQuery();
    }
}

public void run()
{
    int tabNumber = 1;

    if (element.args().parm() == address)
    {
        tabNumber = 2;
    }

    super();

    if (element.args().parm() == addressTab)
    {
        tabAddress.setFocus();
    }

    if (custTableConfigure != null && custTableConfigure.parmNewCustomer())
    {
        custTable_ds.create();
    }

    //
    // If this form is called from Home/Global Address Book then we need to retrieve
    // the full buffer of DirPartyTable so that all the dependent tables will have
    // data along with parent table otherwise the fields related to DirPerson will be
    // disabled and will show text as Unretrieved
    //
    if (element.args().caller())
    {
        if (element.args().caller().name() == formStr(DirPartyTable))
        {
            dirPartyTable.data(DirPartyTable::findRec(callerRecord.RecId));
            dirPartyTable_ds.rereadReferenceDataSources();

            if (dirPartyTable.type() == DirPartyType::Person)
            {
                //
                // Fetch the existing name record and populate the table buffer with it so that
                // it does not attempt to create a new date effective version record for the table
                //
                dirPersonName.data(DirPersonName::find(DirPartyTable.RecId));
            }
        }
    }

    setPartyType = true;
    element.updateDesign();

    if (RetailSMB::IsRetailSMBEnabled())
    {
        RetailUtils::HidePart(element, identifierStr(CustRecurrenceInvoiceInfoPart));
    }
}

public boolean selectControl(FormControl _control)
{
    boolean ret;
    if (dirPartyTable is DirPerson)
    {
        ret = partyForm.selectControl(_control, dirPersonName);

        if (!ret)
        {
            return false;
        }
    }

    ret = super(_control);

    return ret;
}

void setCustomsGroupVisible_IN()
{
    if (!custTable.isForeign())
    {
        taxInformationCustTable_IN.IsPreferential = NoYes::No;
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, IsPreferential)).allowEdit(false);
    }
    else
    {
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, IsPreferential)).allowEdit(true);
    }
}

void setTaxWithholdEnabled_IN()
{
    if (TaxWithholdParameters_IN::checkTaxParameters())
    {
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, tdsGroup)).enabled(!taxInformationCustTable_IN.tcsGroup);
        taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, tcsGroup)).enabled(!taxInformationCustTable_IN.tdsGroup);
    }
}

void setVisible_IN()
{
    TaxParameters taxParametersLoc;

    if (!TaxParameters::find().Customs_IN)
    {
        taxInformationCustTable_IN_DS.object(fieldNum(TaxInformationCustTable_IN, IsPreferential)).visible(false);
    }
    else
    {
        taxInformationCustTable_IN_DS.object(fieldNum(TaxInformationCustTable_IN, IsPreferential)).visible(true);
    }
    taxParametersLoc = TaxParameters::find();
    tabTaxInformation_IN.visible(TaxParameters::checkTaxParameters_IN() || TaxWithholdParameters_IN::checkTaxParameters());

    custTable_ForeignCustomer_IN.visible(taxParametersLoc.Customs_IN);
    custTable_PreferentialCustomer_IN.visible(taxParametersLoc.Customs_IN);
}

public void updateControls()
{
    boolean         newRecord = (custTable.RecId == 0);
    boolean         newPartyRecord = (dirPartyTable.RecId == 0);
    boolean enableTransactions = CustTable::existTransactions(custTable.AccountNum);
    boolean existGlobalTransactions = enableTransactions || CustTable::existTransactionsInMyLegalEntities(custTable.Party);

    if (!newPartyRecord)
    {
        addressBooksCtrl.set(dirPartyTable.addressBooks());
    }

    typeComboBox.select(dirPartyTable.baseType());

    type.allowEdit(newRecord && newPartyRecord);

    lastPartyType = any2Enum(typeComboBox.selection());

    mibTransactions.enabled(enableTransactions);
    TransactionsCrossCompany.enabled(existGlobalTransactions);
    mibSalesTableNew.enabled(custTable.Blocked != CustVendorBlocked::All);
    mibSalesQuotationTable.enabled(SalesQuotationTable::existSales_custAccount(custTable.AccountNum));
    mibCustQuotationJournal.enabled(CustQuotationJour::exist_custAccount(custTable.AccountNum));
    mibCustQuotationJournalConfirmation.enabled(CustQuotationConfirmJour::exist_custAccount(custTable.AccountNum));
    mibLeadsList.enabled(DirPartyTableHelper::isLead(custTable.Party));
    mibOpportunityList.enabled(DirPartyTableHelper::isOpportunity(custTable.Party));

    btnSmmContactPersonSynchronize.visible(!smmOutlookSyncDeprecationHandler::isOutlookSyncDisabled());
}

public void updateDesign()
{
    DirPartyType partyType = dirPartyTable.type();
    boolean      isInternalOrg = (partyType != DirPartyType::Organization && partyType != DirPartyType::Person);
    boolean      isOrganization = (partyType == DirPartyType::Organization);
    boolean      isPerson = (partyType == DirPartyType::Person);

    if (isPerson)
    {
        CustTable_Name.label("@AccountsReceivable:PersonName");
        Org_Name.label("@AccountsReceivable:PersonName");
    }

    if (isOrganization || isInternalOrg)
    {
        CustTable_Name.label("@AccountsReceivable:OrganizationName");
        Org_Name.label("@AccountsReceivable:OrganizationName");
    }

    if (tabGeneral.isActivePage())
    {
        // Organization
        orgGroup1.visible(!isPerson);
        orgGroup2.visible(isOrganization);
        orgPhoneticGroup.visible(isOrganization);
        orgGroup3.visible(!isPerson);

        // Person
        personGroup1.visible(isPerson);
        personGroup2.visible(isPerson);
        personGroup3.visible(isPerson);
        personGroup4.visible(isPerson);

        hideShowGroup.visible(!isInternalOrg);
    }

    if (!isInternalOrg && (setPartyType || tabGeneral.isActivePage()))
    {
        if (setPartyType)
        {
            partyForm.parmPartyType(partyType);
            setPartyType = false;
        }
        partyForm.setDatasourcePartySubType(isPerson ? dirPartyTable_DirPerson_ds : dirPartyTable_DirOrganizationBase_ds);
        partyForm.manageFields();
    }

    B2B.visible(RetailB2BEcommerceFeature::isEnabled());
}

/// <summary>
/// Find the newly created record and switch the form to details view edit mode.
/// </summary>
public void setContext(CustTable custTableRecord)
{
    if (CustCreatePerformanceImprovementFlight::instance().isEnabled())
    {
        Query q = CustTable_ds.query();

        // Apply the range query to have cursor applied on newly created CustTable record through filter.
        SysQuery::findOrCreateRange(q.dataSourceTable(tableNum(CustTable)), fieldNum(CustTable, AccountNum)).value(queryValue(custTableRecord.AccountNum));
    }
    else
    {
        // Lookup the specified customer by using the lookup record on the CustTable table.
        element.args().lookupRecord(custTableRecord);
    }

    CustTable_ds.executeQuery();

    if (CustTable.RecId == custTableRecord.RecId)
    {
        // Switch the form to details view and edit mode
        this.task(#taskSwitchToDetailsView);
        if (!this.viewEditModeHelper().isInEditMode())
        {
            this.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
        }
    }
}

