ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = ExportToExcelDataEntityContext::constructDefault(_menuItem.dataEntityName());

    if (_menuItem.id() == CustomerFilteredEntityOfficeMenuId)
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(_menuItem.dataEntityName());

        ListEnumerator entities = context.entities().getEnumerator();

        while (entities.moveNext())
        {
            if (entities.current().entityName() == _menuItem.dataEntityName())
            {
                entities.current().filter(filterBuilder.and(filterBuilder.companyFilter(),
                        filterBuilder.areEqual(fieldStr(CustCustomerV3Entity, CustomerAccount), custTable.AccountNum)));

                break;
            }
        }
    }
    else if (_menuItem.id() == CustomerMandateEntityOfficeMenuId)
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(_menuItem.dataEntityName());

        ListEnumerator entities = context.entities().getEnumerator();

        while (entities.moveNext())
        {
            if (entities.current().entityName() == _menuItem.dataEntityName())
            {
                entities.current().filter(filterBuilder.and(filterBuilder.companyFilter(),
                    filterBuilder.areEqual(fieldStr(CustDirectDebitMandateEntity, CustomerAccount), custTable.AccountNum)));

                break;
            }
        }
    }

    return context;
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    var dataEntityEnumerator = _menuOptions.dataEntityOptions().getEnumerator();

    while (dataEntityEnumerator.moveNext())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityEnumerator.current();

        if (dataEntityOptions.dataEntityName() == tableStr(CustCustomerV3Entity) ||
            dataEntityOptions.dataEntityName() == tableStr(CustDirectDebitMandateEntity))
        {
            dataEntityOptions.includeDefault(false);
        }
    }

    element.initializeEntityMenuItems();

    _menuOptions.customMenuItems().addEnd(unFilteredCustCustomerEntityMenuItem);
    _menuOptions.customMenuItems().addEnd(filteredCustCustomerEntityMenuItem);
    _menuOptions.customMenuItems().addEnd(directDebitMandateMenuItem);

    element.enableOpenInOfficeMenuItems();
}

public void enableOpenInOfficeMenuItems()
{
    var isDetailsPageActive = TabPageDetails.isActivePage();

    if (filteredCustCustomerEntityMenuItem)
    {
        filteredCustCustomerEntityMenuItem.visible(isDetailsPageActive);
    }

    if (directDebitMandateMenuItem)
    {
        directDebitMandateMenuItem.visible(isDetailsPageActive);
    }

    if (unFilteredCustCustomerEntityMenuItem)
    {
        unFilteredCustCustomerEntityMenuItem.visible(!isDetailsPageActive);
    }
}

private void initializeEntityMenuItems()
{
    unFilteredCustCustomerEntityMenuItem = OfficeGeneratedExportMenuItem::construct(tableStr(CustCustomerV3Entity), CustomerEntityOfficeMenuId);
    unFilteredCustCustomerEntityMenuItem.displayName(strFmt("@AccountsReceivable:CustTableFormOpenInExcelOfficeLabelCustomers", curExt()));

    filteredCustCustomerEntityMenuItem = OfficeGeneratedExportMenuItem::construct(tableStr(CustCustomerV3Entity), CustomerFilteredEntityOfficeMenuId);
    filteredCustCustomerEntityMenuItem.displayName(strFmt("@AccountsReceivable:CustTableFormOpenInExcelOfficeLabelCustomer", curExt()));

    directDebitMandateMenuItem = OfficeGeneratedExportMenuItem::construct(tableStr(CustDirectDebitMandateEntity), CustomerMandateEntityOfficeMenuId);
    directDebitMandateMenuItem.displayName("@AccountsReceivable:CustTableDirectDebitMandateOfficeMenu");
}

/// <summary>
/// Updates the estimated value on open sales orders related to customer.
/// </summary>
/// <param name = "_newRecord">
/// Indicates whether it is a new record.
/// </param>
protected void updateEstimatedValueOnOpenSalesOrderRelatedToCustomer(NoYes _newRecord)
{
    CustCreditLimit custCreditLimit = CustCreditLimit::construct(custTable);
    if (!_newRecord
        && creditMaxChange
        && custCreditLimit.shouldCheckCreditAvailable()
        && custTable.existOpenOrders())
    {
        //Updating estimated value for open sales orders related to the customer.
        Args args = new Args();
        args.parm(custTable.AccountNum);
        MenuFunction salesCreditLimitCheck = new MenuFunction(menuitemActionStr(salesCreditLimitCheck), MenuItemType::Action);
        if (salesCreditLimitCheck.checkAccessRights())
        {
            salesCreditLimitCheck.run(args);
        }
        creditMaxChange = false;
    }
}

/// <summary>
/// Handles a change in a change-controlled field.
/// </summary>
/// <param name = "_fieldNumCust">The number of the changed field in <c>CustTable</c>.</param>
/// <param name = "_fieldNumProp">The number of the changed field in <c>CustTableChangeProposal</c>.</param>
/// <param name = "_custTable">The pointer to a buffer to manipulate as a result of the change.</param>
/// <param name = "_controlledField">The enumeration representing the changed field. Used to check if change control is enabled on the field.</param>
/// <returns>True if changes have been reverted due to change control, otherwise false.</returns>
[Hookable(false)]
public boolean modifiedControlledField(FieldId _fieldNumCust, FieldId _fieldNumProp, CustTable _custTable, CustTableChangeProposalField _controlledField)
{
    CustTable originalCustTable = _custTable.orig();
    boolean ret;

    if (_custTable
            && originalCustTable.(_fieldNumCust) != _custTable.(_fieldNumCust)
            && CustTableChangeProposalFieldEnablement::isEnabledFor(_controlledField))
    {
        if (_custTable.canModifyChangeProposal())
        {
            ttsbegin;
            // create or update an approval entry
            CustTableChangeProposal change = CustTableChangeProposal::findOrCreate(_custTable.RecId, true);
            change.(_fieldNumProp) = _custTable.(_fieldNumCust);
            change.setFlagForChangedField(_controlledField);
            change.update();
            ttscommit;
        }

        // reset the field to original (must be after change proposal creation)
        _custTable.(_fieldNumCust) = originalCustTable.(_fieldNumCust);

        if (custTable_ds.validateWrite())
        {
            custTable_ds.write();
            // pop dialog
            element.showCustChangeProposalDialog();
        }

        ret = true;
    }

    return ret;
}

/// <summary>
/// Adds sales documents to the <c>PrintMgmtSetupContext</c>.
/// </summary>
/// <param name = "_context">The <c>PrintMgmtSetupContext</c> object.</param>
/// <param name = "_salesDocs">To add other sales document types, this param can be overridden.</param>
protected void printMgmtAddSalesDocuments(PrintMgmtSetupContext _context, List _salesDocs = new List(Types::Enum))
{
    _salesDocs.addEnd(PrintMgmtDocumentType::Confirmation);
    _salesDocs.addEnd(PrintMgmtDocumentType::Quotation);
    _salesDocs.addEnd(PrintMgmtDocumentType::SalesFreeTextInvoice);
    _salesDocs.addEnd(PrintMgmtDocumentType::SalesOrderConfirmation);
    _salesDocs.addEnd(PrintMgmtDocumentType::SalesOrderInvoice);
    _salesDocs.addEnd(PrintMgmtDocumentType::SalesOrderPackingSlip);
    _salesDocs.addEnd(PrintMgmtDocumentType::CustAccountStatement);

    if (isConfigurationkeyEnabled(configurationKeyNum(CustInterest)))
    {
        _salesDocs.addEnd(PrintMgmtDocumentType::CustInterestNote);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(CustCollectionLetter)))
    {
        _salesDocs.addEnd(PrintMgmtDocumentType::CustCollectionLetter);
    }

    _salesDocs.addEnd(PrintMgmtDocumentType::SalesAgreementConfirmation);
    _salesDocs.addEnd(PrintMgmtDocumentType::CustPaymAdvice);

    // <GEEHU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        _salesDocs.addEnd(PrintMgmtDocumentType::TaxReimburstmentSlip_HU);
    }
    // </GEEHU>
    // <GEEU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ, #isoHU, #isoPL]))
    {
        _salesDocs.addEnd(PrintMgmtDocumentType::SalesAdvanceInvoice);
    }
    // </GEEU>

    _context.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::CustTable, _salesDocs);
}

/// <summary>
/// Adds project documents to the <c>PrintMgmtSetupContext</c>.
/// </summary>
/// <param name = "_context">The <c>PrintMgmtSetupContext</c> object.</param>
/// <param name = "_projectDocs">To add other project document types, this param can be overridden.</param>
protected void printMgmtAddProjectDocuments(PrintMgmtSetupContext _context, List _projectDocs = new List(Types::Enum))
{
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        _projectDocs.addEnd(PrintMgmtDocumentType::SIProjInvoice);
        _projectDocs.addEnd(PrintMgmtDocumentType::SIProjInvoiceWithBR);
        _projectDocs.addEnd(PrintMgmtDocumentType::SIManagedProjInvoice);
        _projectDocs.addEnd(PrintMgmtDocumentType::ProjCustRetentionReleaseInvoice);
        _projectDocs.addEnd(PrintMgmtDocumentType::ProjectQuotation);
    }
    else
    {
        _projectDocs.addEnd(PrintMgmtDocumentType::ProjectInvoice);
    }
    _context.addHierarchyContext(PrintMgmtHierarchyType::Project, PrintMgmtNodeType::CustTable, _projectDocs);
}

/// <summary>
/// Adds invent documents to the <c>PrintMgmtSetupContext</c>.
/// </summary>
/// <param name = "_context">The <c>PrintMgmtSetupContext</c> object.</param>
/// <param name = "_inventDocs">To add other invent document types, this param can be overridden.</param>
protected void printMgmtAddInventDocuments(PrintMgmtSetupContext _context, List _inventDocs = new List(Types::Enum))
{
    _inventDocs.addEnd(PrintMgmtDocumentType::InventPickList);
    _context.addHierarchyContext(PrintMgmtHierarchyType::Invent, PrintMgmtNodeType::CustTable, _inventDocs);
}

/// <summary>
/// Launches the print management setup for sales, project and invent documents.
/// </summary>
/// <param name = "_context">To add other document types, this param can be overridden.</param>
protected void launchPrintMgmtSetup(PrintMgmtSetupContext _context = new PrintMgmtSetupContext())
{
    this.printMgmtAddSalesDocuments(_context);
    this.printMgmtAddProjectDocuments(_context);
    this.printMgmtAddInventDocuments(_context);

    _context.parmCaller(this);
    _context.parmReferencedTableBuffer(custTable_ds.cursor());
    _context.parmCountryRegionId(custTable.postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(_context);
}

public int task(int _task)
{
    if (_task == #taskSave && DirPartyTable_ds.validateWrite())
    {
        DirPartyTable_ds.write();
    }            

    if (_task == #taskRefresh)
    {
        CustTable currentRecord = CustTable_ds.cursor().data() as CustTable;
        element.args().lookupRecord(currentRecord);
    }
    else if (_task == #taskNew)
    {
        CustTable cust = CustTable_ds.cursor();

        if (CustTable::isDelayNumberSequenceGeneration())
        {
            info("@AccountsPayable:VendAccountNumberGenerationDelay");
            CustTable_ds.object(fieldnum(CustTable, AccountNum)).allowedit(false);
        }
    }

    return super(_task);
}

public Common docCursor()
{
    Common ret = super();

    if(custTable.RecId)
    {
        ret = CustTable;
    }

    return ret;
}

