    return numSequence.Manual;
}

/// <summary>
/// Show the dialog listing changes to change-controlled fields.
/// </summary>
protected void showCustChangeProposalDialog()
{
    Args args = new Args();
    args.menuItemName(menuItemDisplayStr(CustChangeProposal));
    args.menuItemType(MenuItemType::Display);
    args.record(CustTable);
    args.parm("@AccountsReceivable:NotificationChangesRequireApproval");

    new MenuFunction(menuItemDisplayStr(CustChangeProposal), MenuItemType::Display).run(args);
}

/// <summary>
/// Determines if changes to a customer can be submitted to workflow.
/// </summary>
/// <returns>True if changes can be submitted; otherwise false</returns>
public boolean canSubmitToWorkflow()
{
    boolean ret = true;

    if (!CustTable.RecId)
    {
        ret = false;
    }

    if (CustTable.WorkflowState == CustTableChangeProposalWorkflowState::NotSubmitted)
    {
        CustTableChangeProposal changeProposal;
        changeProposal.disableCache(true);
        select RecId from changeProposal
            where changeProposal.CustTable == CustTable.RecId;
        if (!changeProposal)
        {
            ret = false;
        }
    }
    else
    {
        ret = false;
    }

    return ret;
}

