    RetailCustTable_ds.executeQuery();

    element.refreshHeaderTabPages();
}

public void partyTypeModified_BR()
{
    boolean cnpjCpfEnabled  = false;
    boolean ieNumEnabled    = false;
    boolean ccmNumEnabled   = false;
    boolean nitEnabled      = false;
    boolean inssceiEnabled  = false;
    boolean cnaeEnabled     = false;
    boolean personTypeCustomer                   = false;

    // disable is default
    switch (lastPartyType)
    {
        case DirPartyType::Organization:
        case DirPartyType::OperatingUnit:
        case DirPartyType::LegalEntity:
            cnpjCpfEnabled  = true;
            ieNumEnabled    = true;
            ccmNumEnabled   = true;
            cnaeEnabled     = true;
            break;

        case DirPartyType::Person:
            cnpjCpfEnabled  = true;
            ieNumEnabled    = true;
            ccmNumEnabled   = true;
            nitEnabled      = true;
            inssceiEnabled  = true;
            cnaeEnabled     = true;
            personTypeCustomer = true;
            break;

        case DirPartyType::None:
        case DirPartyType::Team:
        default:
            // all disabled
            break;
    }

    custTable.cnpjcpfNum_BR = cnpjCpfEnabled == false ? '' : custTable.cnpjcpfNum_BR;
    this.object(fieldNum(CustTable, cnpjcpfNum_BR)).allowEdit(cnpjCpfEnabled);

    custTable.ieNum_BR = ieNumEnabled == false ? '' : custTable.ieNum_BR;
    this.object(fieldNum(CustTable, ieNum_BR)).allowEdit(ieNumEnabled);

    custTable.ccmNum_BR = ccmNumEnabled == false ? '' : custTable.ccmNum_BR;
    this.object(fieldNum(CustTable, ccmNum_BR)).allowEdit(ccmNumEnabled);

    custTable.nit_br = nitEnabled == false ? '' : custTable.nit_br;
    this.object(fieldNum(CustTable, nit_br)).allowEdit(nitEnabled);

    custTable.insscei_br = inssceiEnabled == false ? '' : custTable.insscei_br;
    this.object(fieldNum(CustTable, insscei_br)).allowEdit(inssceiEnabled);

    custTable.cnae_br = cnaeEnabled == false ? '' : custTable.cnae_br;
    this.object(fieldNum(CustTable, cnae_br)).allowEdit(cnaeEnabled);

    this.object(fieldNum(CustTable, GenerateIncomingFiscalDocument_BR)).allowEdit(!personTypeCustomer);
}

private void enableINSSCEI_BR(boolean _enabled = true)
{
    if (!_enabled)
    {
        custTable.insscei_br = '';
    }

    this.object(fieldNum(CustTable, insscei_br)).allowEdit(_enabled);
}

void setSuframaFieldAccess_BR()
{
    boolean enabledDisabled = true;

    if (custTable.CustFinalUser_BR || custTable.isForeigner_BR())
    {
        enabledDisabled = false;
        custTable.Suframa_BR = NoYes::No;
        custTable.SuframaNumber_BR = '';
        custTable.SuframaPISCOFINS_BR = NoYes::No;
    }

    custTable_ds.object(fieldNum(CustTable,Suframa_BR)).allowEdit(enabledDisabled);

    if (!custTable.Suframa_BR)
    {
        enabledDisabled = false;
        custTable.SuframaNumber_BR = '';
        custTable.SuframaPISCOFINS_BR = NoYes::No;
    }

    custTable_ds.object(fieldNum(CustTable, SuframaNumber_BR)).allowEdit(enabledDisabled);
    custTable_ds.object(fieldNum(CustTable, SuframaPISCOFINS_BR)).allowEdit(enabledDisabled);

    custTable_ds.refresh();
}

/// <summary>
///     Displays a boolean denoting whether customer is merged.
/// </summary>
/// <param name="_custTable">
///     The <c>CustTable</c> record being checked for merged parent.
/// </param>
/// <returns>
///     true if customer is merged into another; otherwise, false.
/// </returns>
display boolean mcrIsMergedCustomer(CustTable _custTable)
{
    if (_custTable.mcrMergedParent
        && isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        return true;
    }

    return false;
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;
    CustAccount customerAccount;

    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]))
    {
        custTable_ds.object(fieldNum(CustTable, EInvoice)).visible(false);
    }

    if (callerRecord.TableId == tableNum(DirPartyTable))
    {
        // Do not want to bind the customer form to a DirPartyTable buffer that was passed to the form
        this.queryBuildDataSource().clearDynalinks();
    }

    // Instead of utilizing the args.record() passed in to limit the datasource
    // passing in an object parameter that needs to be applied to the range.
    // This was done to allow customer merging to change the customer selected,
    // with the args.record() there was no way to refresh the datasource with all records.
    if (custTableConfigure != null && custTableConfigure.parmModifyCustAccount() != "")
    {
        queryBuildDataSource = this.query().dataSourceTable(tableNum(CustTable));
        queryBuildDataSource.clearRanges();

        customerAccount = custTableConfigure.parmModifyCustAccount();
        queryBuildDataSource.addRange(fieldNum(CustTable,AccountNum)).value(customerAccount);
    }

    custTable_ds.cacheAddMethod(tableMethodStr(CustTable, interCompanyTradingRelationActive));
    custTable_ds.cacheAddMethod(tableMethodStr(CustTable, interCompanyTradingPartnerCompanyID));
    custTable_ds.cacheAddMethod(tableMethodStr(CustTable, interCompanyTradingPartnerAccount));

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(custTable_ds);
        traceFilter.parmFilterDataSource(custTableTmpFilter_ds);
        traceFilter.dataSourceInitPost();

        custTable_ds.cacheAddMethod(tableMethodStr(CustTable, phone));
        custTable_ds.cacheAddMethod(tableMethodStr(CustTable, phoneLocal));
    }

}

public void initValue()
{
    super();

    // If customer form is called from other form
    if (callerRecord)
    {
        switch (callerRecord.TableId)
        {
            // If called by Lead or Opportunity, initialize customer values
            case tableNum(smmLeadTable) :
            case tableNum(smmOpportunityTable) :
                smmCreateEntity::initFromCommon(callerRecord, custTable);

                if (custTable.Party)
                {
                    dirPartyTable.data(DirPartyTable::findRec(custTable.Party));
                    custTable.initFromsmmLeadTable();
                }
                break;

            case tableNum(DirPartyTable) :
                dirPartyTable.data(callerRecord);
                break;
        }

        if (dirPartyTable.RecId)
        {
            if (dirPartyTable.type() == DirPartyType::Person)
            {
                dirPersonName.data(DirPersonName::find(dirPartyTable.RecId));
            }
            custTable.Party = dirPartyTable.RecId;
            custTable.modifiedField(fieldNum(CustTable,Party));
        }
    }
}

public int active()
{
    int ret;
    boolean merged = false;

    ret = super();

    if (ret)
    {


        partyForm.parmPartyType(dirPartyTable.type());
        element.updateControls();
        element.updateDesign();


        // <GIN>
        if (TaxParameters::checkTaxParameters_IN()
            || TaxWithholdParameters_IN::checkTaxParameters())
        {
            element.setVisible_IN();
            if (TaxParameters::find().Customs_IN)
            {
                element.setCustomsGroupVisible_IN();
            }

            element.setTaxWithholdEnabled_IN();
        }
        // </GIN>
        element.enableCreditCardVerification();

        if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)) && custTable.mcrMergedParent)
        {
            merged = true;
            mcrMergeIndicatorStaticText.text(strFmt("@MCR12322",custTable.mcrMergedParent));
        }

        mcrMergedIndicator.visible(merged);
        mibSalesTableNew.enabled(!merged);
        mcrMIBSendCatalog.enabled(!merged);
        mcrMIBCustomerService.enabled(!merged);
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        element.enableRfcCurpFields_MX(custTable);
    }

    if (TaxWithholdingGlobalFeature::isExtendedWHTSupportedInCountryRegionOrParamEnabled()
        || BrazilParameters::isEnabled())
    {
        withholdingTax_TaxWithholdGroup_TH.enabled(custTable.TaxWithholdCalculate_TH);
    }

    if (custCollectionsContact.AccountNum != "")
    {
        custCollectionsContact.AccountNum = custTable.AccountNum;
    }

    if (BrazilParameters::isEnabled())
    {
        this.partyTypeModified_BR();

        custTable_ds.setSuframaFieldAccess_BR();
        custTable_ds.enableForeignerId_BR();
    }

    if (custParameters.EntryCertificateIssuingEnabled_W)
    {
        element.allowEditIssueOwnEntryCertificate_W();
    }

    creditMaxChange = false;

    // Set field restrictions for the shared party fields and the additional customer specific fields registered through the shared call.
    DirPartyRoleIsExternallyMaintained::setRestrictionForExternallyMaintainedFields(CustTable.Party, element);

    // Show/hide view proposal details button
    CustTableChangeProposal proposal = CustTableChangeProposal::findByCustRecId(custTable.RecId);
    custChangeProposal.visible(custTable.RecId != 0 && proposal.RecId != 0);
    CustTable_WorkflowState.visible(custTable.RecId != 0 && proposal.RecId != 0);

    return ret;
}

public void linkActive()
{
    smmResponsibilityGroup  responsibilityGroup;
    CustTable               callerCustTable, callerCustTableRoot;
    FormRun                 callingForm;
    boolean                 moveRecord = false;

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    // Is form called with a record
    if (element.args() && element.args().record())
    {
        callingForm = element.args().caller();

        switch (element.args().dataset())
        {
            // Is the form called from the contact person form
            case tableNum(ContactPerson) :
                this.query().dataSourceTable(tableNum(CustTable)).clearDynalinks();
                this.query().dataSourceTable(tableNum(CustTable)).addDynalink(fieldNum(CustTable,Party), element.args().record(), fieldNum(ContactPerson,ContactForParty));
                break;

            // Is the form called from the activities form
            case tableNum(smmActivities) :
                smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(CustTable)), element.args().record());
                break;

            // Is the form called from the responsibilities form
            case tableNum(smmResponsibilityGroup) :
                // Get the current selected responsibilities record
                responsibilityGroup  = element.args().record();

                // Update query
                smmResponsibility::updateQuery(this.query(),this.table(),responsibilityGroup.ResponsibilityId);
                break;
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter))
        && element.args().dataset() == tableNum(CustTable))
    {
        // check first if the customer has been merged into another customer and show that merged customer instead.
        callerCustTable = element.args().record();
        this.query().dataSourceTable(tableNum(CustTable)).clearDynalinks();
        if (callerCustTable.mcrMergedRoot)
        {
            // Instead of dynalinking to the merged root, just move to that record.
            select firstonly callerCustTableRoot
                where callerCustTableRoot.AccountNum == callerCustTable.mcrMergedRoot;
            element.args().lookupRecord(callerCustTableRoot);
        }
        else
        {
            // Find the record instead of filtering on it.
            element.args().lookupRecord(callerCustTable);
        }
        moveRecord = true;
    }

    super();

    // If needed to move to record, this needs to happen after the super so that the execute query has been called
    if (moveRecord)
    {
        this.mark(1);
    }
}

        public boolean validateWrite()
        {
            boolean         ret;
            CustTable       custTableLocal;
            DialogButton    dialogButton;
            #define.ValidatingCountry("US")

            if (CustTable.AccountNum == '')
            {
                element.numberSeqFormHandler().formMethodDataSourceCreate(true);
            }

            whsCustTable.AccountNum = custTable.AccountNum;
            if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
            {
                retailCustTable.AccountNum = custTable.AccountNum;
            }

            ret = super();

            ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

            unchecked (Uncheck::TableSecurityPermission)
            {
                if (ret)
                {
                    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
                    {
                        if (custTable.IdentificationNumber &&
                         custTable.PartyCountry == #ValidatingCountry &&
                         custTable.orig().IdentificationNumber != custTable.IdentificationNumber ||
                         custTable.orig().PartyCountry != custTable.PartyCountry)
                        {
                            custTableLocal = this.findDuplicatePartyCountryAndIdentificationNumber();

                            if (custTableLocal)
                            {
                                dialogButton = Box::yesNo("@SPS123", DialogButton::Yes);

                                if (dialogButton != DialogButton::Yes)
                                {
                                    ret = false;
                                }
                            }
                        }
                    }

                    partyForm.validateWrite();
                }
            }

            // <GTH>
            if (ret && TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled())
            {
                ret = TaxBranchType::checkTaxBranchByDefaultDimension(custTable.DefaultDimension);
            }
            // </GTH>

            // Validate each field in visible controls
			// This is for copy customer data from another company.
            DictField dictField;
            int fieldCount = 1;

            QueryBuildFieldList fieldList = custTable_ds.query().dataSourceTable(tablenum(CustTable)).fields();

            while (fieldCount <= fieldList.fieldCount())
            {
                dictField = new DictField(tablenum(CustTable), fieldList.field(fieldCount));
                str fieldName = DictField.name();

                // Skip validation on account number and main contact worker
                // Account number is copied or be validated 
                // Main contact worker is validated in cust table validateWrite through super call
                if (dictField.id() != fieldNum(CustTable, AccountNum) && dictField.id() != fieldNum(CustTable, MainContactWorker))
                {
                    if (dictField.id() == fieldNum(CustTable, CustWriteOffRefRecId))
                    {
                        if (custTable.CustWriteOffRefRecId != 0)
                        {
                            if (!CustWriteOffFinancialReasonsSetup::findByRecIDAndCompany(custTable.CustWriteOffRefRecId))
                            {
                                info("@AccountsReceivable:InvalidCustDefaultWriteOffReasonInCurrentCompany");
                                custTable.CustWriteOffRefRecId = 0;
                            }
                        }
                    }

                    if (dictField.id() == fieldNum(CustTable, IRS1099CIndicator))
                    {
                        #ISOCountryRegionCodes
                        boolean isIsoUs = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoUS]);

                        if (!isIsoUs)
                        {
							// Ignore the table security permission when defaulting the IRS1099CIndicator field in non-US countries.
                            unchecked (Uncheck::TableSecurityPermission)
                            {
                                custTable.IRS1099CIndicator = 0;
                            }
                        }
                    }

                    FormDataObject control = CustTable_ds.object(dictField.id());

                    if (control != null && control.visible())
                    {
                        if (!control.validate())
                        {
                            ret = false;
                        }
                    }
                }

                fieldCount++;
            }

            return ret;
        }

private CustTable findDuplicatePartyCountryAndIdentificationNumber()
{
    CustTable custTableLocal;

    select firstonly IdentificationNumber, PartyCountry from custTableLocal
        where custTableLocal.PartyCountry == custTable.PartyCountry &&
            custTableLocal.IdentificationNumber == custTable.IdentificationNumber &&
            (custTableLocal.PartyCountry != '' || custTableLocal.IdentificationNumber != '');

    return custTableLocal;
}

void write()
{
    NoYes  newRecord;

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        retailCustTable.AccountNum = custTable.AccountNum;
    }

    ttsbegin;

    newRecord = custTable.RecId ? NoYes::No : NoYes::Yes;

    custTable.Party = dirPartyTable.RecId;

    whsCustTable.AccountNum = custTable.AccountNum;


    super();

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    if (!mcrCustTable.RecId)
    {
        // need to force the write in the create case since the form won't do it.
        mcrCustTable.CustTable = custTable.RecId;
        mcrCustTable.write();
    }

    // If customer form is called from other form and creating new record
    if (callerRecord && newRecord)
    {
        // If called by Lead or Opportunity, copy associations
        if (callerRecord.TableId == tableNum(smmLeadTable) ||
            callerRecord.TableId == tableNum(smmOpportunityTable))
        {
            smmCreateEntity::createLinkCommon(callerRecord, custTable);
        }
    }

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        taxInformationCustTable_IN.CustTable = custTable.AccountNum;
        taxInformationCustTable_IN.write();
    }
    // </GIN>
    ttscommit;

    element.updateEstimatedValueOnOpenSalesOrderRelatedToCustomer(newRecord);
}

void delete()
{
    FormDataSource callerDataSource;

    ttsbegin;
    element.numberSeqFormHandler().formMethodDataSourceDelete();

    super();
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        mcrPrevActiveCustomer = '';
    }

    if (element.args() && element.args().dataset() == tableNum(smmBusRelTable))
    {
        callerDataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (callerDataSource)
        {
            callerDataSource.executeQuery();
        }
    }

    ttscommit;
}

void create(boolean append = false,
            boolean extern = false)  // If created externally
{
    element.numberSeqFormHandler().formMethodDataSourceCreatePre();

    super(append);

    if (!CustTable::isDelayNumberSequenceGeneration() || element.isNumberSequenceManual())
    {
        if (!extern)
        {
            element.numberSeqFormHandler().formMethodDataSourceCreate(true);
        }
    }
    else
    {
        CustTable cust = CustTable_ds.cursor();

        if (cust.AccountNum == null)
        {
            info("@AccountsReceivable:DelayCustAccountNumberGeneration");
            CustTable_ds.object(fieldnum(CustTable, AccountNum)).allowedit(false);
        }
    }

    isCustGroupValueSetOnce = false;
}

public void enableForeignerId_BR()
{
    this.object(fieldNum(CustTable, ForeignerId_BR)).allowEdit(custTable.isForeigner_BR());
}

public void executeQuery()
{
    container applicableTables = [tableNum(TaxGSTReliefGroupHeading_MY), tableNum(LvPaymTransCodes), tableNum(CompanyNAFCode)];
    QueryLocalizationHelper::excludeLocalizationTablesIfApplicable(
        this.query(),
        applicableTables,
        element.name());

    super();
}

