public boolean validate()
{
    boolean ret;

    CustTable custTableInvoiceAccount;

    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        custTableInvoiceAccount = CustTable::find(custTable.InvoiceAccount);
        // Do not allow invoice account to be set
        // to a merged customer
        if (custTableInvoiceAccount.mcrMergedParent)
        {
            error(strFmt("@MCR12337", custTable.InvoiceAccount, custTableInvoiceAccount.mcrMergedParent));
            ret = false;
        }
    }

    return ret;
}

