        public boolean validateWrite()
        {
            boolean ret = super();

            // Validate each field in visible controls
            // This is for copy customer data from another company.
            DictField dictField;
            int fieldCount = 1;

            QueryBuildFieldList fieldList = CustCollectionsContact_ds.query().dataSourceTable(tablenum(CustCollectionsContact)).fields();

            while (fieldCount <= fieldList.fieldCount())
            {
                dictField = new DictField(tablenum(CustCollectionsContact), fieldList.field(fieldCount));

                if (dictField.id() != fieldNum(CustCollectionsContact, AccountNum))
                {
                    FormDataObject control = CustCollectionsContact_ds.object(dictField.id());
                    if (control != null && control.visible())
                    {
                        if (dictField.id() == fieldNum(CUSTCOLLECTIONSCONTACT, ContactPersonId))
                        {
							// CustCollectionsContact.ContactPersonId need to be handled in code.
							// The invalid value will cause error message and user cannot fix it through form.
                            if (CustCollectionsContact.ContactPersonId != '')
                            {
                                if (ContactPerson::find(CustCollectionsContact.ContactPersonId) == null)
                                {
                                    warning(strFmt("@AccountsReceivable:InvalidContactPersonInCurrentCompany", curext()));
                                    CustCollectionsContact.ContactPersonId = '';
                                }
                            }
                        }

                        if (!control.validate())
                        {
                            ret = false;
                        }
                    }
                }

                fieldCount++;
            }

            return ret;
        }

