public void modified()
{

    // Change control
    DirPartyName originalName = DirPartyTable.orig().Name;
    DirPartyName newName = DirPartyTable.Name;

    if (CustTable
        && originalName != newName
        && CustTableChangeProposalFieldEnablement::isEnabledFor(CustTableChangeProposalField::Name))
    {
        DirPartyTable.Name = originalName;

        if (CustTable.canModifyChangeProposal())
        {
            // Create or update an approval entry
            ttsbegin;
            CustTableChangeProposal change = CustTableChangeProposal::findOrCreate(CustTable.RecId, true);
            change.Name = newName;
            change.update();
            ttscommit;
        }

        // pop dialog
        element.showCustChangeProposalDialog();
    }

    super();
    if (!custTable.RecId && dirPartyTable.RecId)
    {
        partyForm.modifiedName();
        custTable.Party = dirPartyTable.RecId;
        if (DirPartyTableHelper::isvendor(custTable.Party))
        {
            custTable.VendAccount = VendTable::findByPartyRecId(custTable.Party).AccountNum;
        }
        element.updateControls();
    }
}

