public edit MCRScriptID displayTitleTranslation(boolean _set, MCRMessageAssociation _mcrMessageAssociation, MCRScriptID _scriptTitle)
{
    // Get the RecId for the given script title for a new record
    if (_set)
    {
        _mcrMessageAssociation.mcrScript = MCRScriptTranslation::findByScriptTitleAndLanguage(_scriptTitle, SystemParameters::getSystemLanguageId()).mcrScript;
        return _scriptTitle;
    }

    // Display the record's translated script title
    return _mcrMessageAssociation.getMessageScriptTitle();
}

public boolean validateWrite()
{
    boolean ret;

    // Make this data source read only if respective localization is not active.
    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        return false;
    }

    ret = super();
    if (ret && TaxWithholdParameters_IN::checkTaxParameters() )
    {
        if (custTable.TaxWithholdCalculate_IN
            && !taxInformationCustTable_IN.tdsGroup
            && !taxInformationCustTable_IN.tcsGroup)
        {
            ret = ret && checkFailed("@GLS5850");
        }
    }
    return ret;
}

public void write()
{
    // Only write if respective localization is active
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        super();
    }
}

public int active()
{
    int ret;

    ret = super();
    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        if (ret)
        {
            taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, panNumber)).enabled(taxInformationCustTable_IN.PanStatus == PANStatus_IN::Received);
            taxInformationCustTable_IN_ds.object(fieldNum(TaxInformationCustTable_IN, panReferenceNumber)).enabled(taxInformationCustTable_IN.PanStatus == PANStatus_IN::Applied);
        }
    }
    // </GIN>

    return ret;
}

public void init()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void modified()
