public void init()
{
    if (!isConfigurationkeyEnabled(new DictTable(this.table()).configurationKeyId()))
    {
        this.linkType(FormLinkType::Passive);
    }

    super();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // Validate each field in visible controls
    // This is for copy customer data from another company.
    DictField dictField;
    int fieldCount = 1;

    QueryBuildFieldList fieldList = RetailCustTable_ds.query().dataSourceTable(tablenum(RetailCustTable)).fields();

    while (fieldCount <= fieldList.fieldCount())
    {
        dictField = new DictField(tablenum(RetailCustTable), fieldList.field(fieldCount));

        if (dictField.id() != fieldNum(RetailCustTable, AccountNum))
        {
            FormDataObject control = RetailCustTable_ds.object(dictField.id());
            if (control != null && control.visible())
            {
                if (!control.validate())
                {
                    ret = false;
                }
            }
        }

        fieldCount++;
    }

    // Validate receipt email address.
    if (RetailCustTable.receiptEmail && !RetailCustTable::validateReceiptEmail(RetailCustTable.receiptEmail))
    {
        ret = checkFailed("@Retail:InvalidReceiptEmail");
    }

    return ret;
}

public void setMandateButtonEnabled()
{
    var mandateStatus = custDirectDebitMandate.getStatus();

    CancelMandate.enabled(mandateStatus != CustMandateStatus::Canceled);

    ResetMandate.enabled(mandateStatus == CustMandateStatus::Active);
}

public int active()
{
    int ret;

    ret = super();

    this.setMandateButtonEnabled();

    return ret;
}

