    }
}

public int active()
{
    int ret;

    ret = super();

    if (MCROrderParameters::find().mcrFraudCheck)
    {
        mcrBtnHold.enabled(mcrCustTable.CustStatus != MCRCustStatus::Fraudulent);
        mcrBtnFraudClear.enabled(mcrCustTable.CustStatus == MCRCustStatus::Fraudulent);
    }

    // Allow these fields to be edited as long as they are blank.  As soon as they're set they cannot be changed
    mcrCustTable_ds.object(fieldNum(MCRCustTable, OrigSourceId)).allowEdit(mcrCustTable.OrigSourceId == '');

    return ret;
}

public void create(boolean _append = false)
{
    if (!mcrCustTable.RecId
        && !mcrCustTable.CustTable)
    {
        super(_append);

        // Show that source is mandatory if source code requires, validateWrite will handle validation.
        if (MCROrderParameters::find().mcrSourceCodeRequired)
        {
            this.object(fieldNum(MCRCustTable, OrigSourceId)).mandatory(true);
        }
    }
    else
    {
        element.createRecord(custTable_ds.name());
    }
}

