public boolean validateWrite()
{
    boolean ret = super();

    // Validate each field in visible controls
    // This is for copy customer data from another company.
    DictField dictField;
    int fieldCount = 1;

    QueryBuildFieldList fieldList = WHSCustTable_ds.query().dataSourceTable(tablenum(WHSCustTable)).fields();

    while (fieldCount <= fieldList.fieldCount())
    {
        dictField = new DictField(tablenum(WHSCustTable), fieldList.field(fieldCount));

        if (dictField.id() != fieldNum(WHSCustTable, AccountNum))
        {
            FormDataObject control = WHSCustTable_ds.object(dictField.id());
            if (control != null && control.visible())
            {
                if (!control.validate())
                {
                    ret = false;
                }
            }
        }

        fieldCount++;
    }

    return ret;
}

