public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        DirPartyType selected = any2Enum(typeComboBox.selection());

        if (selected != lastPartyType)
        {
            custTable_ds.delete();
            partyForm.parmPartyType(selected);
            partyForm.create();
            lastPartyType = selected;
        }
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        custTable_ds.partyTypeModified_BR();
    }
    // </GBR>

    return ret;
}

        public boolean modified()
        {
            boolean ret;

            ret = super();

            CustTable customer = CustTable_ds.cursor() as CustTable;
            CustGroup cGroup = CustGroup::find(customer.CustGroup);

			// Account number can be changed when creating a new record without copying.
            if (!customer.RecId && CustTable::isDelayNumberSequenceGeneration() && !isCopied) 
            {
                if (element.isNumberSequenceManual())
                {
                    CustTable_ds.object(fieldnum(CustTable, AccountNum)).allowedit(true);
                }
                else
                {
                    CustTable_ds.object(fieldnum(CustTable, AccountNum)).setValue('');
                    CustTable_ds.object(fieldnum(CustTable, AccountNum)).allowedit(false);
                }
            }

            return ret;
        }

