public boolean modified()
{
    boolean ret;

    ret = super();

    CustTable customer = CustTable_ds.cursor() as CustTable;

    if (customer.RecId)
    {
        if (customer.PrepayType == SalesPrepayType::Percent)
        {
            if (customer.PrepaymentValue < 0 || customer.PrepaymentValue > 100)
            {
                ret = checkFailed("@SYS183656");
            }
        }
        else if (customer.PrepayType == SalesPrepayType::Fixed)
        {
            if (customer.PrepaymentValue < 0)
            {
                ret = checkFailed("@SYS183656");
            }
        }
    }

    return ret;
}

