void clicked()
{
    if (Box::yesNo("@SYS4002547", DialogButton::Yes) == DialogButton::Yes)
    {
        // We don't want system to proceed with cancellation of mandate if it is being used as default;
        if (custTable.DefaultDirectDebitMandate == custDirectDebitMandate.RecId)
        {
            throw error("@AccountsReceivable:MandateUsedAsDefaultForCustomer");
        }

        CustDirectDebitMandate::cancelMandate(custDirectDebitMandate.RecId);
        custDirectDebitMandate_ds.reread();
        custDirectDebitMandate_ds.refresh();
        custDirectDebitMandate_ds.setMandateButtonEnabled();
    }
}

void clicked()
{
    if (Box::yesNo("@SYS4002590", DialogButton::Yes) == DialogButton::Yes)
    {
        CustDirectDebitMandate::resetMandate(custDirectDebitMandate.RecId);
        custDirectDebitMandate_ds.reread();
        custDirectDebitMandate_ds.refresh();
        custDirectDebitMandate_ds.setMandateButtonEnabled();
    }
}

