public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret && SearchString.valueStr())
    {
        SearchString.text('');
        SearchString.modified();
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        Query query = CustTable_ds.queryRun().query();

        this.clearPreviousSearchQuery(query);

        if (this.valueStr())
        {
            guid sessionId = newGuid();
            UserId userId = curUserId();

            ttsbegin;

            List custTableRecIds = CustTableSearch::search(CustTableSearchType.selection(), this.text(), sessionId, userId);

            if (custTableRecIds != null && !custTableRecIds.empty())
            {
                if (custTableRecIds.elements() <= CustTableSearch::MAX_QUERY_SEARCH_RESULTS)
                {
                    str range  = con2Str(list2Con(custTableRecIds), ',');
                    SysQuery::findOrCreateRange(query.dataSourceTable(tableNum(CustTable)),fieldNum(CustTable, RecId)).value(range);
                }
                else
                {
                    QueryBuildDataSource qbdsCustTableCustomSearchResult = query.dataSourceTable(tableNum(CustTableCustomSearchResult));

                    if (!qbdsCustTableCustomSearchResult)
                    {
                        QueryBuildDataSource qbdsCustTableCustomSearchResultTmp = query.dataSourceTable(tableNum(CustTable)).addDataSource(tableNum(CustTableCustomSearchResult));

                        qbdsCustTableCustomSearchResultTmp.joinMode(JoinMode::InnerJoin);
                        qbdsCustTableCustomSearchResultTmp.addLink(fieldNum(CustTable, RecId), fieldNum(CustTableCustomSearchResult, CustTable));
                    }
                    else
                    {
                        qbdsCustTableCustomSearchResult.joinMode(JoinMode::InnerJoin);
                    }

                    SysQuery::findOrCreateRange(query.dataSourceTable(tableNum(CustTableCustomSearchResult)),fieldNum(CustTableCustomSearchResult, SessionId)).value(guid2Str(sessionId));
                    SysQuery::findOrCreateRange(query.dataSourceTable(tableNum(CustTableCustomSearchResult)),fieldNum(CustTableCustomSearchResult, UserId)).value(userId);
                }
            }
            else
            {
                SysQuery::findOrCreateRange(query.dataSourceTable(tableNum(CustTable)),fieldNum(CustTable, RecId)).value(SysQuery::valueEmptyString());
            }

            CustTable_ds.query(query);
            CustTable_ds.executeQuery();

            CustTableSearch::cleanCustomSearchResultBySessionId(sessionId, userId);

            ttscommit;
        }
        else
        {
            CustTable_ds.query(query);
            CustTable_ds.executeQuery();
        }
    }

    return ret;
}

private void clearPreviousSearchQuery(Query _custTableDSQuery)
{
    QueryBuildDataSource qbdsCustTableCustomSearchResult = _custTableDSQuery.dataSourceTable(tableNum(CustTableCustomSearchResult));

    if (qbdsCustTableCustomSearchResult)
    {
        qbdsCustTableCustomSearchResult.joinMode(JoinMode::OuterJoin);
        qbdsCustTableCustomSearchResult.clearRanges();
    }

    QueryBuildDataSource qbdsCustTable = _custTableDSQuery.dataSourceTable(tableNum(CustTable));
    qbdsCustTable.clearRange(fieldNum(CustTable, RecId));
}

public void jumpRef()
{
    element.task(#taskSwitchToDetailsView);
}
