/* PostR1AXLTODO: (Code Upgrade) Group control CustOverview_Amount had TopMode property set to BottomEdge. This property is no longer respected. Make any necessary adjustments to correct the position of the group control. */
[Form]
public class CustVendCompensation extends FormRun
{
    private const str LocalizationFeatureReference = 'EEU-00010';

    CustTransDetails custTransDetails;
    CustVendTransDetails custVendTransDetails;

    [FormObservable]
    AmountCur totalDebitAmount;
    [FormObservable]
    AmountCur totalCreditAmount;
    [FormObservable]
    AmountCur totalDiffAmount;

    QueryBuildRange queryRangeAccountNum;
    QueryBuildRange queryRangeCurrecyCode;

    boolean useCust;

    RecId ledgerCustRecId;
    RecId ledgerVendRecId;

}

public void addRanges(QueryBuildDataSource _ds, TableId _tableId)
{
    // Select only open transactions for the matching currency for a specific account
    _ds.addRange(fieldName2id(_tableId, identifierStr(Closed))).value(strFmt('%1..', dateNull()));
    queryRangeAccountNum = _ds.addRange(fieldName2id(_tableId, identifierStr(AccountNum)));
    queryRangeCurrecyCode = _ds.addRange(fieldName2id(_tableId, identifierStr(CurrencyCode)));
}

public void init()
{
    FormDataSource fds;
    QueryBuildDataSource ds;
    FieldId fieldIdOfTransRecId;
    CustVendTrans trans;
    AccountNum relatedAccountNum;

    if (!element.args().caller() || !element.args().record())
    {
        throw warning(strFmt("@SYS22678", element.name()));
    }

    useCust = element.args().record().TableId == tableNum(CustTrans);

    super();

    //
    // Setup query based on the record passed (CustTrans or VendTrans)
    // e.g. if CustTrans passed, setup query for VendTrans to match
    //
    fds = FormDataUtil::getFormDataSource(element.args().record());
    trans = fds.cursor();
    relatedAccountNum = useCust ?
            CustTable::find(trans.AccountNum).VendAccount :
            CustTable::findByVendor(trans.AccountNum).AccountNum;

    if (!relatedAccountNum)
    {
        throw error("@GLS223089");
    }

    queryRangeCurrecyCode.value(trans.CurrencyCode);
    queryRangeAccountNum.value(relatedAccountNum);

    ds = useCust ? custTrans_Q.dataSourceTable(trans.TableId) : vendTrans_q.dataSourceTable(trans.TableId);

    fieldIdOfTransRecId = fieldName2id(trans.TableId, identifierStr(RecId));

    for (trans = getFirstSelection(fds); trans; trans = fds.getNext())
    {
        ds.addRange(fieldIdOfTransRecId).value(queryValue(trans.RecId));
    }

    //
    // Setup the form to work with Cust trans or Vend trans
    // on the top of the form, the other at the bottom.
    // Only the grid at the bottom will have selectable records.
    //
    custGrid.showRowLabels(!useCust);
    vendGrid.showRowLabels(useCust);

    if (useCust)
    {
        markedDebitAmount.label("@SYS16704");
        markedCreditAmount.label("@SYS11523");
    }
    else
    {
        markedDebitAmount.label("@SYS11523");
        markedCreditAmount.label("@SYS16704");
        this.design().moveControl(custTab.id(), vendTab.id());
        vendTab.height(custTab.heightValue(), custTab.heightMode());
        custTab.height(0, 1);
    }
}

public AmountCur totalOfMarkedTrans(FormDataSource _ds)
{
    CustVendTrans trans;
    AmountCur total = 0;

    for (trans = getFirstSelection(_ds); trans; trans = _ds.getNext())
    {
        if (_ds.markRecord(trans))
        {
            total += trans.AmountCur - trans.SettleAmountCur;
        }
    }
    return total;
}

public AmountCur totalOfTrans(FormDataSource _ds)
{
    CustVendTrans trans;
    AmountCur total = 0;

    for (trans = _ds.getFirst(); trans; trans = _ds.getNext())
    {
        total += trans.AmountCur - trans.SettleAmountCur;
    }
    return total;
}

public void updateTotalCreditAmount(FormDataSource _ds)
{
    totalCreditAmount = element.totalOfMarkedTrans(_ds);
    totalDiffAmount = totalDebitAmount + totalCreditAmount;
    markedDiffAmount.update();
    markedCreditAmount.update();
}

public void executeQuery()
{
    super();

    if (useCust)
    {
        totalDebitAmount = element.totalOfTrans(custTrans_ds);
        element.updateTotalCreditAmount(vendTrans_ds);
    }
}

public void init()
{
    QueryBuildDataSource ds;

    super();

    ds = this.query().dataSourceTable(tableNum(CustTrans));
    ds.clearDynalinks();

    if (!useCust)
    {
        element.addRanges(ds, tableNum(CustTrans));
    }

    custTransDetails = new CustTransDetails(custTrans);
}

public void executeQuery()
{
    super();

    if (!useCust) // Vend Trans on top, get total of all records
    {
        totalDebitAmount = element.totalOfTrans(vendTrans_ds);
        element.updateTotalCreditAmount(custTrans_ds);
    }
}

public void init()
{
    QueryBuildDataSource ds;

    super();

    ds = this.query().dataSourceTable(tableNum(VendTrans));
    ds.clearDynalinks();

    if (useCust)
    {
        element.addRanges(ds, tableNum(VendTrans));
    }

    custVendTransDetails = new CustVendTransDetails(vendTrans);
}

void clicked()
{
    GlobalizationInstrumentationHelper::featureRun(LocalizationFeatureReference, funcName());

    Dialog dialog = new Dialog("@GLS223575");
    DialogField journalNumField;
    DialogField newJournalNameField;
    DialogField transDateField;
    DialogField printCompensationField;
    CustVendCompensation custVendCompensation;

    dialog.addGroup("@SYS25908");
    newJournalNameField = dialog.addField(identifierStr(LedgerJournalNameIdDaily), "@SYS6790", "@GLS223576");
    journalNumField = dialog.addField(identifierStr(JournalNumNotPosted), "@SYS328705");
    transDateField = dialog.addField(identifierStr(TransDate), "@GLS110044");
    printCompensationField = dialog.addField(identifierStr(NoYesId), "@GLS110190");

    if (dialog.run())
    {
        custVendCompensation = new CustVendCompensation();
        custVendCompensation.parmSetJournalName(newJournalNameField.value());
        custVendCompensation.parmSetJournalNum(journalNumField.value());
        custVendCompensation.parmSetTransDate(transDateField.value());

        custVendCompensation.parmSetData(useCust, custTrans_ds, vendTrans_ds);
        [ledgerCustRecId, ledgerVendRecId] = custVendCompensation.run();

        if (printCompensationField.value())
        {
            new MenuFunction(menuitemOutputStr(CustVendCompensationReport), MenuItemType::Output).run(new Args(element));
        }

        element.close();
    }
}
