[Form]
public class CustVendInvoiceSettlement_IT extends FormRun
{
    public CustVendInvoiceSettlementBuild_IT CustVendInvoiceSettlementBuild;
    public NoYes onlyOpen, includeTransOpen;
    public Map invoiceBalancesMap;
    public CustVendTransDetails custVendTransDetails;

    [FormObservable]
    public CustVendTable masterData;
    public ModuleCustVend module;


}

public CustVendInvoiceSettlementBuild_IT getCustVendInvoiceSettlementBuild()
{
    return CustVendInvoiceSettlementBuild;
}

public Common getTransTable(CustVendTrans _custVendTrans = custVendtrans)
{
    Common transTable;

    switch(_custVendTrans.TableId)
    {
        case tableNum(CustTrans):
            CustTrans custTrans = _custVendTrans;
            transTable = custTrans;
            break;

        case tableNum(VendTrans):
            VendTrans vendTrans = _custVendTrans;
            transTable = vendTrans;
            break;

        default:
            transTable = _custVendTrans;
    }

    return transTable;
}

public void init()
{
    Voucher voucher;
    InvoiceId invoiceId;
    CustVendAc custVendAc;

    super();

    CustVendInvoiceSettlementBuild = new CustVendInvoiceSettlementBuild_IT();

    if (this.args() && this.args().parmEnumType())
    {
        module = this.args().parmEnum();
    }

    if (this.args().record())
    {
        switch (this.args().record().TableId)
        {
            case tablenum(CustTable):
                module = ModuleCustVend::Cust;
                CustTable custTable = this.args().record();
                custVendAc = custTable.AccountNum;
                break;

            case tablenum(CustInvoiceJour):
                module = ModuleCustVend::Cust;
                CustInvoiceJour custInvoiceJour = this.args().record();
                custVendAc = custInvoiceJour.InvoiceAccount;
                voucher = custInvoiceJour.LedgerVoucher;
                invoiceId = custInvoiceJour.invoiceId;
                break;

            case tablenum(vendTable):
                module = ModuleCustVend::Vend;
                VendTable vendTable = this.args().record();
                custVendAc = vendTable.AccountNum;
                break;

            case tablenum(VendInvoiceJour):
                module = ModuleCustVend::Vend;
                VendInvoiceJour vendInvoiceJour = this.args().record();
                custVendAc = vendInvoiceJour.InvoiceAccount;
                voucher = vendInvoiceJour.LedgerVoucher;
                invoiceId = vendInvoiceJour.invoiceId;
                break;
        }
    }

    custVendInvoiceSettlementBuild.parmModule(module);
    custVendInvoiceSettlementBuild.parmCustVendAccount(custVendAc);
    custVendInvoiceSettlementBuild.parmInvoiceVoucher(voucher);
    custVendInvoiceSettlementBuild.parminvoiceId(invoiceId);

    CustTransOpen.visible(module == ModuleCustVend::Cust);
    VendTransOpen.visible(module == ModuleCustVend::Vend);

    custVendTransDetails = new CustVendTransDetails(custVendInvoiceSettlementBuild.getTransTable());

    if (module == ModuleCustVend::Cust)
    {
        masterData = SysDictTable::newTableId(tableNum(CustTable)).makeRecord();
    }
    else
    {
        masterData = SysDictTable::newTableId(tableNum(VendTable)).makeRecord();
    }

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceIT00046, funcName());
}

public void refreshData()
{
    CustVendTempInvoiceList_IT_ds.executeQuery();
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    CustVendTempInvoiceList_IT item = _record;

    if (item.Open)
    {
        _options.textColor(WinAPI::RGB2int(220,0,20));
    }
    else
    {
        _options.textColor(WinAPI::RGB2int(0,0,0));
    }

    _options.affectedElementsByField(fieldId2Ext(fieldnum(CustVendTempInvoiceList_IT, OpenAmountMST),1));
    super(_record, _options);
}

public int active()
{
    int ret = super();

    if (CustVendTempInvoiceList_IT.CustVendAC != masterData.AccountNum)
    {
        masterData = CustVendInvoiceSettlementBuild.getMasterDataTableTable(CustVendTempInvoiceList_IT);
    }

    return ret;
}

public void executeQuery(boolean buildInvoices = true)
{            
    this.query().dataSourceTable(tablenum(CustVendTempInvoiceList_IT)).clearDynalinks();

    if (buildInvoices)
    {
        custVendTempInvoiceList_IT.setTmpData(CustVendInvoiceSettlementBuild.BuildCustVendTempInvoiceList());
    }

    CustVendTrans_ds.executeQuery();
    super();
}

display NoYes isPrepayment(CustVendTrans _custVendTrans)
{
    CustTrans custTrans;

    if (_custVendTrans.TableId == custTrans.TableId)
    {
        custTrans = _custVendTrans;
        return custTrans.Prepayment;
    }

    return NoYes::No;
}

public int active()
{
    int ret = super();

    custVendTransDetails.setCustVendTrans(custVendTrans);

    return ret;
}

[SysClientCacheDataMethodAttribute(true)]
display BalanceStr_IT currentBalance(CustVendTrans _custVendTrans)
{
    if (invoicebalancesMap.exists(_custVendTrans.RecId))
    {
        return num2str(InvoicebalancesMap.lookup(_custVendTrans.RecId),15,2,-1,-1);
    }
    else
    {
        return CustVendInvoiceSettlementBuild_IT::noBalanceStr();
    }
}

public void executeQuery()
{
    CustVendTrans   localCustVendTrans;

    this.query().dataSourceTable(tablenum(CustVendTrans)).clearDynalinks();

    if (custVendTempInvoiceList_IT)
    {
        custVendTrans.setTmpData(CustVendInvoiceSettlementBuild.BuildCustVendTmpInvoiceSettlement(custVendTempInvoiceList_IT));
        invoiceBalancesMap = CustVendInvoiceSettlementBuild.getBalancesMap();
    }
    else
        custVendTrans.setTmpData(localCustVendTrans);

    super();

}

