/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator iterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (iterator.more())
    {
        OfficeMenuDataEntityOptions currentOption = iterator.value();

        if (this.isEntityValidForExport(moduleCustVend, currentOption.dataEntityName()))
        {
            iterator.next();
        }
        else
        {
            iterator.delete();
        }
    }
}

private boolean isEntityValidForExport(ModuleCustVend _module, DataEntityName _dataEntityName)
{
    boolean isValid = true;

    switch (_module)
    {
        case ModuleCustVend::Cust:
            if (_dataEntityName != tableStr(CustomerPaymentJournalLineEntity))
            {
                isValid = false;
            }
            break;

        case ModuleCustVend::Vend:
            if (_dataEntityName != tableStr(VendorPaymentJournalLineEntity))
            {
                isValid = false;
            }
            break;
    }

    return isValid;
}

void bookResearch(boolean _findRecordAgain = false)
{
    LedgerJournalTrans findLedgerJournalTrans;
    RecId findRecId = ledgerJournalTrans.RecId;

    ledgerJournalTrans_ds.research();

    if (_findRecordAgain)
    {
        findLedgerJournalTrans = LedgerJournalTrans::findRecId(findRecId, false);

        element.args().lookupRecord(findLedgerJournalTrans);
        ledgerJournalTrans_ds.research();
    }
}

